/** Decoder **/

// decode payload to string
var data = decodeToString(payload);

// deviceName
var deviceName =  metadata.topic.split("/")[1];

// pattern
var patterncar = 'frigate/'+deviceName+'/car';
var patternperson = 'frigate/'+deviceName+'/person';
var patternbicycle = 'frigate/'+deviceName+'/bicycle';
var patterncarsnapshot = 'frigate/'+deviceName+'/car/snapshot';
var patternpersonsnapshot = 'frigate/'+deviceName+'/person/snapshot';
var patternbicyclesnapshot = 'frigate/'+deviceName+'/bicycle/snapshot';

switch (metadata.topic) {
    case patterncar:
        var car = data;
        break;
    case patternperson:
        var person = data;
        break;
    case patternbicycle:
        var bicycle = data;
        break;
    case patterncarsnapshot:
        var carsnapshot = payload;
        break;
    case patternpersonsnapshot:
        var personsnapshot = payload;
        break;
    case patternbicyclesnapshot:
        var bicyclesnapshot = payload;
        break;
    default:
}

var deviceType = 'sensor';

// Result object with device attributes/telemetry data
var result = {
    deviceName: deviceName,
    deviceType: deviceType,
    telemetry: {
       car: car,
       person: person,
       bicycle: bicycle,
       carsnapshot: carsnapshot,
       personsnapshot: personsnapshot,
       bicyclesnapshot: bicyclesnapshot
   }
};

/** Helper functions **/

function decodeToString(payload) {
   return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
   // convert payload to string.
   var str = decodeToString(payload);

   // parse string to JSON
   var data = JSON.parse(str);
   return data;
}



return result;

