// decode payload to JSON
var deviceType = 'sensor';

// Result object with device attributes/telemetry data
var result = {
    deviceName: deviceName,
    deviceType: deviceType,
    telemetry: {
       stProcData: data.stProcData,
       stProcTime: data.stProcTime,
       idScavo: data.idScavo,
       pressSpinta: data.pressSpinta,
       lunghScavo: data.lunghScavo,
       allarme_off: array(data.dword68,1),
       preallarme_off: array(data.dword68,2),
       ins_tubo: array(data.dword68,3),
       fine_scavo: array(data.dword68,4),
       spinta_avanti: array(data.dword68,5),
       spinta_indietro: array(data.dword68,6),
       rotazione_Attiva: array(data.dword68,7)
   }
};

/** Helper functions **/

function decodeToString(payload) {
   return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
   // convert payload to string.
   var str = decodeToString(payload);

   // parse string to JSON
   var data = JSON.parse(str);
   return data;
}

function array(x,y){
    var z=y-1;
    var multiplier = (Math.pow(2, z));
    if ((x & multiplier)==0){
        return false;
    }
    else{
        return true;
    }
}


return result;
