var data=decodeToJson(payload);
var deviceName="eui-"+data.deviceInfo.devEui;
var deviceType="sensor";
var result;
var light;
var occupancy;
var motion;
var battery;
var latitude;
var longitude;
var status;
var sos;
var shock;
var firmware;
var ts;
var manomissione;
var temperature;
var valve;
var LowBatteryAlarm;
var EmptyPipe;
var ReverseFlow;
var OverRange;
var TemperatureAlarm;
var EEError;
var LeakageAlarm;
var BurstAlarm;
var BatteryOver;
var BatteryWarning;
var BatteryError;
var AccuracyUnsafe;
var HardwareError;
var EmptyTube;
var FlowOutOfRange;
var Sleep;
var NotProtected;
var NdcModuleBatteryWarning;
var NdcModuleConnectionLost;
var current;
var leakage_status;
//water_leak
if(data.object.leakage_status!=null){
    if(data.object.leakage_status=="leak"){leakage_status=1;}
    else if(data.object.leakage_status=="normal"){leakage_status=0;}
    else{leakage_status=0;}
}
//sensecap
var sensecap_array;
if(data.object.messages!=null){sensecap_array=data.object.messages[0];}
//manomissione
if(data.object.tamper_status!=null){
    if(data.object.tamper_status=="uninstalled"){manomissione=1;}
    else{manomissione=0;}
}
//current
if(data.object.data.object.in1_ac_A!=null){current=data.object.in1_ac_A;}
if(data.object.data.object.current!=null){current=data.object.current;}
//open/close
if(data.object.magnet_status!=null){
    if(data.object.magnet_status=="open"){status=1;}
    else{status=0;}
}
//temperature
if(data.object.temperature!=null){temperature=data.object.temperature;}
//light
if(data.object.light!=null){light=Math.round(data.object.light*100)/100;}
if(data.object.light_level!=null){light=Math.round(data.object.light_level*100)/100;}
//battery
if(data.object.battery!=null){battery=Math.round(data.object.battery*100)/100;}
//battery from vdd
if(data.object.vdd!=null){
    var mvolts=data.object.vdd;
    if(mvolts>=3000){battery=100;}
    else if(mvolts>2900){battery=100-((3000-mvolts)*58)/100;}
    else if(mvolts>2740){battery=42-((2900-mvolts)*24)/160;}
    else if(mvolts>2440){battery=18-((2740-mvolts)*12)/300;}
    else if(mvolts>2100){battery=6-((2440-mvolts)*6)/340;}
    else{battery=0;}
}
//motion
if(data.object.motion!=null){motion=Math.round(data.object.motion*100)/100;}
//occupancy
if(data.object.occupancy!=null){occupancy=Math.round(data.object.occupancy*100)/100;}
//Bove valve
if(data.object.ValveStatus!=null){
    if(data.object.ValveStatus=="close"){valve=1;}
    else if(data.object.ValveStatus=="open"){valve=0;}
}
if(data.object.LowBatteryAlarm!=null){LowBatteryAlarm=1;}
if(data.object.EmptyPipe!=null){EmptyPipe=1;}
if(data.object.ReverseFlow!=null){ReverseFlow=1;}
if(data.object.OverRange!=null){OverRange=1;}
if(data.object.TemperatureAlarm!=null){TemperatureAlarm=1;}
if(data.object.EEError!=null){EEError=1;}
if(data.object.LeakageAlarm!=null){LeakageAlarm=1;}
if(data.object.BurstAlarm!=null){BurstAlarm=1;}
//Zenner
if(data.object.BatteryOver=="BatteryOver"){BatteryOver=1;}
if(data.object.BatteryWarning=="BatteryWarning"){BatteryWarning=1;}
if(data.object.BatteryError=="BatteryError"){BatteryError=1;}
if(data.object.AccuracyUnsafe=="AccuracyUnsafe"){AccuracyUnsafe=1;}
if(data.object.HardwareError=="HardwareError"){HardwareError=1;}
if(data.object.EmptyTube=="EmptyTube"){EmptyTube=1;}
if(data.object.FlowOutOfRange=="FlowOutOfRange"){FlowOutOfRange=1;}
if(data.object.Sleep=="Sleep"){Sleep=1;}
if(data.object.NotProtected=="NotProtected"){NotProtected=1;}
if(data.object.NdcModuleBatteryWarning=="NdcModuleBatteryWarning"){NdcModuleBatteryWarning=1;}
if(data.object.NdcModuleConnectionLost=="NdcModuleConnectionLost"){NdcModuleConnectionLost=1;}
//tracker
if(sensecap_array!=null){
    for(var i = 0; i < sensecap_array.length; i++){
        if(sensecap_array[i].type=="Battery"){battery=Math.round(sensecap_array[i].measurementValue*100)/100;}
        if(sensecap_array[i].type=="Latitude"){
            latitude=sensecap_array[i].measurementValue;
            if(sensecap_array[i].timestamp!=null){ts=sensecap_array[i].timestamp;}
        }
        if(sensecap_array[i].type=="Longitude"){longitude=sensecap_array[i].measurementValue;}
        if(sensecap_array[i].type=="SOS Event"){sos=sensecap_array[i].measurementValue;}
        if(sensecap_array[i].type=="Air Temperature"){temperature=sensecap_array[i].measurementValue;}
        if(sensecap_array[i].type=="Light"){light=Math.round(sensecap_array[i].measurementValue*100)/100;}
        if(sensecap_array[i].type=="Firmware Version"){firmware=sensecap_array[i].measurementValue;}
        if(sensecap_array[i].type=="Timestamp"){ts=sensecap_array[i].measurementValue;}
        if(sensecap_array[i].type=="Event Status"){
            var temporaneamente=sensecap_array[i].measurementValue;
            if(sensecap_array[i].measurementValue.length>0){
                for(var j=0;j<temporaneamente.length;j++){
                    if(temporaneamente[j].eventName=="SOS event."){sos=1;}
                    if(temporaneamente[j].eventName=="Shock event."){shock=1;}
                }
            }
        }
    }
}

if(ts!=null){
    result = {
        deviceName: deviceName,
        deviceType: deviceType,
        attributes: {
            DevEUI: data.deviceInfo.devEui,
            fPort: data.fPort,
            firmware: firmware,
            first_hour: data.object.first_hour,
            total_liter: data.object.liter,
            valve: valve,
        },
        telemetry: {
            ts: ts,
            values: {
                battery: battery,
                latitude: latitude,
                longitude: longitude,
                status: status,
                sos: sos,
                shock: shock,
                temperature: temperature,
                humidity: data.object.humidity,
                light: light,
                motion: motion,
                soundAvg: data.object.soundAvg,
                soundPeak: data.object.soundPeak,
                vdd: data.object.vdd,
                pressEvent: data.object.pressEvent,
                batteryVoltage: data.object.batteryVoltage,
                co2: data.object.co2,
                formaldeide: data.object.hcho,
                pir: data.object.pir,
                occupancy: occupancy,
                pm10: data.object.pm10,
                pm2_5: data.object.pm2_5,
                pressure: data.object.pressure,
                tvoc: data.object.tvoc,
                thermistorProperlyConnected: data.object.thermistorProperlyConnected,
                water: data.object.water,
                power: data.object.in1_ac_A*230,
                current: current,
                leakage_status: leakage_status,
                liter: data.object.liter,
                first_hour: data.object.first_hour,
                manomissione: manomissione,
                string: data.object.string,
                valve: valve,
                LowBatteryAlarm: LowBatteryAlarm,
                EmptyPipe: EmptyPipe,
                ReverseFlow: ReverseFlow,
                OverRange: OverRange,
                TemperatureAlarm: TemperatureAlarm,
                EEError: EEError,
                LeakageAlarm: LeakageAlarm,
                BurstAlarm: BurstAlarm,
                BatteryOver: BatteryOver,
                BatteryWarning: BatteryWarning,
                BatteryError: BatteryError,
                AccuracyUnsafe: AccuracyUnsafe,
                HardwareError: HardwareError,
                EmptyTube: EmptyTube,
                FlowOutOfRange: FlowOutOfRange,
                Sleep: Sleep,
                NotProtected: NotProtected,
                NdcModuleBatteryWarning: NdcModuleBatteryWarning,
                NdcModuleConnectionLost: NdcModuleConnectionLost,
            }
        }
    };
}else{
    result = {
        deviceName: deviceName,
        deviceType: deviceType,
        attributes: {
            DevEUI: data.deviceInfo.devEui,
            fPort: data.fPort,
            firmware: firmware,
            first_hour: data.object.first_hour,
            total_liter: data.object.liter,
            valve: valve,
        },
        telemetry: {
            battery: battery,
            latitude: latitude,
            longitude: longitude,
            status: status,
            sos: sos,
            shock: shock,
            temperature: temperature,
            humidity: data.object.humidity,
            light: light,
            motion: motion,
            soundAvg: data.object.soundAvg,
            soundPeak: data.object.soundPeak,
            vdd: data.object.vdd,
            pressEvent: data.object.pressEvent,
            batteryVoltage: data.object.batteryVoltage,
            co2: data.object.co2,
            formaldeide: data.object.hcho,
            pir: data.object.pir,
            occupancy: occupancy,
            pm10: data.object.pm10,
            pm2_5: data.object.pm2_5,
            pressure: data.object.pressure,
            tvoc: data.object.tvoc,
            thermistorProperlyConnected: data.object.thermistorProperlyConnected,
            water: data.object.water,
            power: data.object.in1_ac_A*230,
            current: current,
            leakage_status: leakage_status,
            liter: data.object.liter,
            first_hour: data.object.first_hour,
            manomissione: manomissione,
            string: data.object.string,
            valve: valve,
            LowBatteryAlarm: LowBatteryAlarm,
            EmptyPipe: EmptyPipe,
            ReverseFlow: ReverseFlow,
            OverRange: OverRange,
            TemperatureAlarm: TemperatureAlarm,
            EEError: EEError,
            LeakageAlarm: LeakageAlarm,
            BurstAlarm: BurstAlarm,
            BatteryOver: BatteryOver,
            BatteryWarning: BatteryWarning,
            BatteryError: BatteryError,
            AccuracyUnsafe: AccuracyUnsafe,
            HardwareError: HardwareError,
            EmptyTube: EmptyTube,
            FlowOutOfRange: FlowOutOfRange,
            Sleep: Sleep,
            NotProtected: NotProtected,
            NdcModuleBatteryWarning: NdcModuleBatteryWarning,
            NdcModuleConnectionLost: NdcModuleConnectionLost,
        }
    };
}



function decodeToString(payload) {
    return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
    var str = decodeToString(payload);
    var data = JSON.parse(str);
    return data;
}

return result;
