configuration.decoder:
// Decode an uplink message from a buffer
// payload - array of bytes
// metadata - key/value object

/** Decoder **/

// decode payload to string
var payloadStr = decodeToString(payload);

// decode payload to JSON
// var data = decodeToJson(payload);

var deviceName = 'Device A';
var deviceType = 'thermostat';
var customerName = 'Customer C';
var groupName = 'thermostat devices';
var manufacturer = 'Example corporation';
// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// Result object with device/asset attributes/telemetry data
var result = {
// Use deviceName and deviceType or assetName and assetType, but not both.
   deviceName: deviceName,
   deviceType: deviceType,
// assetName: assetName,
// assetType: assetType,
// customerName: customerName,
   groupName: groupName,
   attributes: {
       model: 'Model A',
       serialNumber: 'SN111',
       integrationName: metadata['integrationName'],
       manufacturer: manufacturer
   },
   telemetry: {
       temperature: 42,
       humidity: 80,
       rawData: payloadStr
   }
};

/** Helper functions **/

function decodeToString(payload) {
   return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
   // covert payload to string.
   var str = decodeToString(payload);

   // parse string to JSON
   var data = JSON.parse(str);
   return data;
}

return result;

configuration.tbelDecoder:
// Parse JSON safely
var json = decodeToJson(payload);
if (json == null) {
  json = {};
}

// Decide which root to use
var plc = null;
var suffix = ""; // used for renaming
if (json.plcData != null) {
  plc = json.plcData;
  suffix = "";  // normal case
} else if (json.plcData1 != null) {
  plc = json.plcData1;
  suffix = "1"; // alternate case
} else {
  plc = json;
}

// Colors (null-safe)
var colors = (plc.colors != null) ? plc.colors : {};

// Telemetry
var telemetry = {};

// Core sensor data
if (suffix === "") {
  telemetry.temperature = plc.temperature;
  telemetry.humidity    = plc.humidity;
} else {
  telemetry["temperature" + suffix] = plc.temperature;
  telemetry["humidity" + suffix]    = plc.humidity;
}

// RGB defaults to 0 if missing
telemetry.R = (colors.R != null) ? colors.R : 0;
telemetry.G = (colors.G != null) ? colors.G : 0;
telemetry.B = (colors.B != null) ? colors.B : 0;

// Interval: ms -> seconds
if (plc.Interval != null) {
  telemetry.Interval = (plc.Interval * 1.0) / 1000.0;
}

// Optional fields
if (plc.voc != null) {
  telemetry.voc = plc.voc;
}
if (plc.ventola != null) {
  telemetry.ventola = plc.ventola;
}
if (plc.riscaldante != null) {
  telemetry.riscaldante = plc.riscaldante;
}

// -------- RELAYS (flat only) --------
if (plc.relays != null) {
  var i = 1;
  var MAX_RELAYS = 16; // adjust as needed
  while (i <= MAX_RELAYS) {
    var key = "" + i;
    if (plc.relays[key] != null) {
      telemetry["relay" + i] = plc.relays[key] * 1;
    }
    i = i + 1;
  }
}

// Attributes (empty for now)
var attributes = {};

return {
  deviceName: "M5",
  deviceType: "test",
  attributes: attributes,
  telemetry: telemetry
};
