/**
 * Decode an uplink message from a buffer
 * @param {Array} payload - array of bytes (not used here)
 * @param {Object} metadata - key/value object with sensor data
 * @returns {Object} result - telemetry and device info
 */
function decodeUplink(payload, metadata) {
    // Device identification (customize as needed)
    var deviceName = 'M5';
    var deviceType = 'test';

    // Compose result
    var result = {
        deviceName: deviceName,
        deviceType: deviceType,
        attributes: {
            // You can add static or dynamic attributes here if needed
        },
        telemetry: {
            temperature: metadata.temperature,
            humidity: metadata.humidity,
            voc: metadata.voc,
            Interval: metadata.Interval,
            ventola: metadata.ventola,
            riscaldante: metadata.riscaldante,
            R: metadata.colors && metadata.colors.R !== undefined ? metadata.colors.R : null,
            G: metadata.colors && metadata.colors.G !== undefined ? metadata.colors.G : null,
            B: metadata.colors && metadata.colors.B !== undefined ? metadata.colors.B : null
        }
    };

    return result;
}
