configuration.tbelDecoder:
// Parse JSON safely
var json = decodeToJson(payload);
if (json == null) {
  json = {};
}

// Telemetry object
var telemetry = {};

// ---- ENV: temperature & humidity ----
if (json.env != null) {
  var env = json.env;
  if (env.temperature != null) telemetry.temperature = env.temperature;
  if (env.humidity    != null) telemetry.humidity    = env.humidity;
}

// ---- RELAYS: flattened relay1..relay16 ----
if (json.relays != null) {
  var r = json.relays;
  var i = 1;
  var MAX_RELAYS = 16;
  while (i <= MAX_RELAYS) {
    var key = "" + i;
    if (r[key] != null) {
      telemetry["relay" + i] = r[key] * 1; // force number
    }
    i = i + 1;
  }
}

// ---- COLOR: R/G/B (default to 0 only when color is present & field missing) ----
if (json.colors != null) {
  var c = json.colors;
  telemetry.R = (c.R != null) ? c.R : 0;
  telemetry.G = (c.G != null) ? c.G : 0;
  telemetry.B = (c.B != null) ? c.B : 0;
}

// ---- TVOC ----
if (json.voc != null) {
  telemetry.voc = json.voc; // keep the same 'voc' key in telemetry as before
}

// ---- INTERVAL: ms -> seconds ----
if (json.interval != null) {
  telemetry.Interval = (json.interval * 1.0) / 1000.0;
}

// Attributes (empty for now)
var attributes = {};

return {
  deviceName: "lab1 station2",
  deviceType: "test",
  attributes: attributes,
  telemetry: telemetry
};
