var data = decodeToJson(payload);
var deviceName = data.end_device_ids.device_id;
var deviceType = data.end_device_ids.application_ids.application_id;

var result = {
    deviceName: deviceName,
    deviceType: deviceType,
    telemetry: {
         temperature: data.uplink_message.decoded_payload.temperature,
         humidity: data.uplink_message.decoded_payload.humidity,
         light: data.uplink_message.decoded_payload.light,
         motion: data.uplink_message.decoded_payload.motion,
         soundAvg: data.uplink_message.decoded_payload.soundAvg,
         soundPeak: data.uplink_message.decoded_payload.soundPeak,
         vdd: data.uplink_message.decoded_payload.vdd,
         pressEvent: data.uplink_message.decoded_payload.pressEvent,
         batteryVoltage: data.uplink_message.decoded_payload.batteryVoltage,
         thermistorProperlyConnected: data.uplink_message.decoded_payload.thermistorProperlyConnected,
         dev_latitude: data.uplink_message.rx_metadata[0].location.latitude,
         dev_longitude: data.uplink_message.rx_metadata[0].location.longitude
    }
};

function decodeToString(payload) {
    return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
    var str = decodeToString(payload);
    var data = JSON.parse(str);
    return data;
}

return result;

