configuration.decoder:
var data = decodeToJson(payload);
var deviceName = data.end_device_ids.device_id;
var deviceType = data.end_device_ids.application_ids.application_id;

var result = {
    deviceName: deviceName,
    deviceType: deviceType,
    telemetry: {
         temperature: data.uplink_message.decoded_payload.temperature,
         humidity: data.uplink_message.decoded_payload.humidity,
         light: data.uplink_message.decoded_payload.light,
         motion: data.uplink_message.decoded_payload.motion,
         soundAvg: data.uplink_message.decoded_payload.soundAvg,
         soundPeak: data.uplink_message.decoded_payload.soundPeak,
         vdd: data.uplink_message.decoded_payload.vdd,
         pressEvent: data.uplink_message.decoded_payload.pressEvent,
         batteryVoltage: data.uplink_message.decoded_payload.batteryVoltage,
         thermistorProperlyConnected: data.uplink_message.decoded_payload.thermistorProperlyConnected,
         dev_latitude: data.uplink_message.rx_metadata[0].location.latitude,
         dev_longitude: data.uplink_message.rx_metadata[0].location.longitude
    }
};

function decodeToString(payload) {
    return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
    var str = decodeToString(payload);
    var data = JSON.parse(str);
    return data;
}

return result;

configuration.tbelDecoder:
var data = decodeToJson(payload);
var deviceName = data.end_device_ids.device_id;
var deviceType = "sensor";
var light;
var battery;

var sensecap_array=data.uplink_message.decoded_payload.messages[0];

//light
if(data.uplink_message.decoded_payload.light!=null){
    light=data.uplink_message.decoded_payload.light;
}
if(data.uplink_message.decoded_payload.light_level!=null){
    light=data.uplink_message.decoded_payload.light_level;
}
//battery
if(sensecap_array[3].measurementValue!=null){
    battery=sensecap_array[3].measurementValue;
}
if(data.uplink_message.decoded_payload.battery!=null){
    battery=data.uplink_message.decoded_payload.battery;
}

var result = {
    deviceName: deviceName,
    deviceType: deviceType,
    attributes: {
    },
    telemetry: {
        temperature: data.uplink_message.decoded_payload.temperature,
        humidity: data.uplink_message.decoded_payload.humidity,
        light: light,
        motion: data.uplink_message.decoded_payload.motion,
        soundAvg: data.uplink_message.decoded_payload.soundAvg,
        soundPeak: data.uplink_message.decoded_payload.soundPeak,
        vdd: data.uplink_message.decoded_payload.vdd,
        pressEvent: data.uplink_message.decoded_payload.pressEvent,
        batteryVoltage: data.uplink_message.decoded_payload.batteryVoltage,
        co2: data.uplink_message.decoded_payload.co2,
        formaldeide: data.uplink_message.decoded_payload.hcho,
        pir: data.uplink_message.decoded_payload.pir,
        occupancy: data.uplink_message.decoded_payload.occupancy,
        pm10: data.uplink_message.decoded_payload.pm10,
        pm2_5: data.uplink_message.decoded_payload.pm2_5,
        pressure: data.uplink_message.decoded_payload.pressure,
        tvoc: data.uplink_message.decoded_payload.tvoc,
        thermistorProperlyConnected: data.uplink_message.decoded_payload.thermistorProperlyConnected,
        water: data.uplink_message.decoded_payload.water,
        battery: battery,
        latitude: sensecap_array[2].measurementValue,
        longitude: sensecap_array[1].measurementValue,
        status: sensecap_array[0].measurementValue[0].eventName,
    }
};

function decodeToString(payload) {
    return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
    var str = decodeToString(payload);
    var data = JSON.parse(str);
    return data;
}

return result;
