var json = decodeToJson(payload);
var plc = json.plcData;
if (plc == null) {
    plc = {};
}

var telemetry = {};
telemetry["inp1"]    = plc.inp1 != null ? plc.inp1 : 0;
telemetry["relay1"]  = plc.relay1 != null ? plc.relay1 : 0;
telemetry["relay2"]  = plc.relay2 != null ? plc.relay2 : 0;
telemetry["relay3"]  = plc.relay3 != null ? plc.relay3 : 0;
telemetry["key"]     = plc.key != null ? plc.key : 0;
telemetry["reset"]   = plc.reset != null ? plc.reset : 0;

var attributes = {};

return {
    "deviceName": "M5",
    "deviceType": "test",
    "attributes": attributes,
    "telemetry": telemetry
}
