configuration.tbelDecoder:
// Decode an uplink message from a buffer
// payload - array of bytes
// metadata - key/value object

/** Decoder **/

// decode payload to JSON
var data = decodeToJson(payload);

// Use the plcData object inside the JSON
var plc = data.plcData || {};

var deviceName = 'M5';
var deviceType = 'test';

// Coerce booleans/undefined to number
function asNum(val) {
    if (typeof val === "boolean") return val ? 1 : 0;
    if (typeof val === "number") return val;
    if (typeof val === "string") return val === "true" ? 1 : 0;
    return 0;
}

var result = {
   deviceName: deviceName,
   deviceType: deviceType,
   attributes: {},
   telemetry: {
      inp1:    asNum(plc.inp1),
      relay1:  asNum(plc.relay1),
      relay2:  asNum(plc.relay2),
      relay3:  asNum(plc.relay3),
      key:     asNum(plc.key),
      reset:   asNum(plc.reset)
   }
};

return result;
