settings.colorFunction:
var type = dsData[dsIndex]['Type'];
if (type == 'colorpin') {
	var temperature = dsData[dsIndex]['temperature'];
	if (typeof temperature !== undefined) {
	    var percent = (temperature + 60)/120 * 100;
	    return tinycolor.mix('blue', 'red', amount = percent).toHexString();
	}
	return 'blue';
}

settings.labelFunction:
var deviceType = dsData[dsIndex]['Type'];
if (typeof deviceType !== undefined) {
    if (deviceType == "energy meter") {
        return '<span style="color:orange;">${entityName}, ${energy:2} kWt</span>';
    } else if (deviceType == "thermometer") {
        return '<span style="color:blue;">${entityName}, ${temperature:2} °C</span>';
    }
}

settings.tooltipFunction:
var content = '<div style="font-family: Roboto;">' +
                 '<div style="font-size: 14px; font-weight: 500; padding-bottom: 8px;">${entityLabel}</div>';
                 
var deviceType = data['Type'];
if (typeof deviceType !== undefined) {
    content += '<div style="font-size: 12px; font-weight: normal;">' + getInfoByDeviceType(deviceType) + '</div>';
}
content += '</div>';
return content;

function getInfoByDeviceType(type) {
    switch (type) {
        case 'Smart Shelf':
            return 'Weight: ${weight:1} Kg';
        case 'Chiller':
        case 'Freezer':    
            return 'Temperature: ${temperature:1} °C';
        case 'Smart Bin':
            return 'Fullness: ${level:0}%';
        case 'Liquid Level Sensor':
            return 'Liquid level: ${level:0}%';
        case 'Door Sensor':
            var open = data['open'] === 'true';
            return 'State: ' + (open ? 'Open' : 'Closed');
        case 'Motion Sensor':
            var movement = data['movement'] === 'true';
            return 'Movement detection: ' + (movement ? 'Detected' : 'None');
        case 'Occupancy Sensor':
            var occupied = data['occupied'] === 'true';
            return 'State: ' + (occupied ? 'Occupied' : 'Vacant');
        case 'Smoke Sensor':
            var alarm = data['alarm'] === 'true';
            return 'Fire alarm: ' + (alarm ? 'Alarm' : 'None');
        default:
            return 'TODO';
    }
}

