settings.cellContentFunction:
var state = value;
var active = entity.active;
if (active === 'false') {
    state = 'disconnected';
}

var stateSvg = getStateSvg(state);
var encodedStateSvg = encodeURIComponent(stateSvg);
var stateSvgUrl = 'data:image/svg+xml,' + encodedStateSvg;
var stateLabel = getStateLabel(state);

return '<span>'+
           '<img style="vertical-align: middle;" class="mat-icon" src="'+ stateSvgUrl +'">' +
           '<span style="vertical-align: middle; padding-left: 8px;">'+ stateLabel +'</span>' +
       '</span>';

function getStateSvg(state) {
    switch (state) {
        case 'critical':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#E64A19"/>' +
                   '</svg>';
        case 'major':            
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#D5C21B"/>' +
                   '</svg>';
        case 'normal':            
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#1F8B4D"/>' +
                   '</svg>';
        case 'disconnected':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<g clip-path="url(#clip0_2839_52564)">' +
                   '<circle cx="10" cy="10" r="6" fill="#CAC1D2"/>' +
                   '<path d="M1.92896 16.728L16.0711 2.58589L17.4853 4.00011L3.34317 18.1422L1.92896 16.728Z" fill="#CAC1D2"/>' +
                   '<path d="M2.10692 16.3171C1.81393 16.0241 1.81393 15.5491 2.10692 15.2561L15.1852 2.17788C15.4781 1.88489 15.9532 1.88489 16.2462 2.17788C16.5392 2.47088 16.5392 2.94591 16.2462 3.2389L3.16794 16.3171C2.87495 16.6101 2.39991 16.6101 2.10692 16.3171Z" fill="white"/>' +
                   '</g>' +
                   '<defs>' +
                   '<clipPath id="clip0_2839_52564">' +
                   '<rect width="20" height="20" fill="white"/>' +
                   '</clipPath>' +
                   '</defs>' +
                   '</svg>';
        default:
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#AAAAAA"/>' +
                   '</svg>';
    }
}

function getStateLabel(state) {
    switch(state) {
        case 'critical':
            return 'Critical';
        case 'major':
            return 'Major';
        case 'normal':
            return 'Normal';
        case 'disconnected':
            return 'Disconnected';
        default:
            return state;
    }
}

