            return 'motion_sensor_settings';
        case 'Occupancy Sensor':
            return 'occupancy_sensor_settings';
        case 'Smoke Sensor':
            return 'smoke_sensor_settings';
        default:
            return 'unknown_settings';
    }
}

function getSettingsHeight(type) {
    switch (type) {
        case 'Smart Shelf':
            return '280px';
        case 'Freezer':            
            return '420px';
        case 'Chiller':            
            return '420px';                  
        case 'Smart Bin':
            return '420px';                  
        case 'Door Sensor':
