            : '<tb-dashboard-state fxFlex [ctx]="ctx" stateId="supermarkets_card"></tb-dashboard-state>') +
          '<mat-divider style="margin-top: 10px; margin-bottom: 10px;"></mat-divider>' +
          '<tb-dashboard-state fxFlex [ctx]="ctx" [syncParentStateParams]="' + (supermarketState ? 'true' : 'false') + '" stateId="'+ (supermarketState ? 'supermarket_alarms' : 'supermarkets_alarms') +'"></tb-dashboard-state>' +
       '</div>';

settings.markdownCss:
.tb-markdown-view .tb-progress-cover, .tb-markdown-view .mat-drawer-container {
    background-color: #fff;
}
.tb-markdown-view div, .tb-markdown-view p {
    line-height: normal;
}

.tb-markdown-view > div {
    padding: 0;
}

.tb-markdown-view table {
    border: none;
    border-radius: 0px;
    overflow: auto;
}

.tb-markdown-view .mat-toolbar.mat-primary div {
    color: var(--tb-primary-contrast-500);
}

.tb-markdown-view .tb-widget-container > .tb-widget {
    border-radius: 0px;
}

.tb-markdown-view .tb-widget-container > .tb-widget .tb-table-widget mat-toolbar {
    height: 65px;
    max-height: 65px;
}


.tb-markdown-view .tb-widget-container > .tb-widget.tb-has-timewindow .tb-table-widget mat-toolbar {
    height: 100px;
    max-height: 100px;
}

.main-layout .header {
    height: 60px;
    margin: 4px;
} 

.main-layout .header .mat-icon.title-icon {
    width: 40px;
    height: 40px;
}

.main-layout .header .title {
    font-size: 18px;
    font-weight: 500;
    color: #28232D;
}

.main-layout .header .subtitle {
    font-size: 13px;
    font-weight: normal;
    color: #757575;
}

config.widgetCss:
.tb-widget-title {
    align-items: stretch !important;
    flex: 1;
}

.tb-widget-actions {
    position: absolute;
    top: 0;
    right: 0;
}

.tb-widget-title tb-timewindow .tb-timewindow {
    border: 1px solid rgba(0, 0, 0, 0.12);
    border-radius: 4px;
    padding: 8px 8px;
    position: relative;
}

.tb-widget-title tb-timewindow .tb-timewindow span {
    flex: 1;
    line-height: 32px;
}

.tb-widget-title tb-timewindow .tb-timewindow::after {
    font-family: "Material Icons";
    content: 'expand_more';
    position: absolute;
    font-size: 24px;
    top: 12px;
    right: 12px;
    z-index: -1;
}

settings.dashboardCss:
.tb-widget-container > .tb-widget {
    border-radius: 8px;
}

gridster-item:not(.tb-noselect) > .tb-widget-container > .tb-widget {
    cursor: default !important;
}

.tb-widget-container > .tb-widget .tb-table-widget .mat-row {
    cursor: pointer;
}

.tb-widget-container > .tb-widget .tb-legend-keys {
    cursor: pointer;
}
