    var percent = (speed - 45)/85;
    if (percent < 0.5) {
        percent *=2*100; 
        return tinycolor.mix('green', 'yellow', percent).toHexString();
    } else {
        percent = (percent - 0.5)*2*100;
        return tinycolor.mix('yellow', 'red', percent).toHexString();
    }
}

