elementClick.customFunction:
console.log($event,entityId,entityName,additionalParams,widgetContext)

let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));

    openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    let vm = instance;
    vm.addEntityFormGroup = vm.fb.group({
     risoluzione: [null, [vm.validators.required]],
     ore_fermo: [null, [vm.validators.required]],
     note: [""],
     eventDate:[null, [vm.validators.required]],
     eventTime:[null, [vm.validators.required]]
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };
    vm.save = function() {
        var formData=vm.addEntityFormGroup.value
        formData.eventDate.setHours(formData.eventTime.getHours())
        formData.eventDate.setMinutes(formData.eventTime.getMinutes())
        console.log("formData: ",formData);
        vm.addEntityFormGroup.markAsPristine();
        var telData={"ts":formData.eventDate.valueOf(), "values":{"risoluzione":formData.risoluzione, "ore_fermo":formData.ore_fermo, "note":formData.note, "guasto":"spintore"}}
        widgetContext.http.post('/api/plugins/telemetry/'+entityId.entityType+'/'+entityId.id+'/timeseries/ANY?scope=ANY', telData).subscribe(data =>
        {
        // console.log("data: ",data)
            // widgetContext.updateAliases();
            vm.dialogRef.close(null);
        },function(error){
            console.log(error)
        })
    };

}

