headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let schedulerEventService = $injector.get(widgetContext.servicesMap.get('schedulerEventService'));

openAddSchedulerEventDialog();

function openAddSchedulerEventDialog() {
    customDialog.customDialog(htmlTemplate, AddSchedulerEventDialogController).subscribe();
}


function AddSchedulerEventDialogController(instance) {
    let vm = instance;

    var currentDate = new Date();
    var startDate = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      currentDate.getDate());
    var startTime = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      currentDate.getDate(),
      0,0,0,0);

    vm.schedulerRepeatTypes = {
        'DAILY': 'scheduler.daily',
        'EVERY_N_DAYS': 'scheduler.every-n-days',
        'WEEKLY': 'scheduler.weekly',
        'EVERY_N_WEEKS': 'scheduler.every-n-weeks',
        'MONTHLY': 'scheduler.monthly',
        'YEARLY': 'scheduler.yearly',
        'TIMER': 'scheduler.timer'
    };


    vm.schedulerRepeatTypeValues = Object.keys(vm.schedulerRepeatTypes);

    vm.schedulerTimeUnits = {
        'HOURS': 'scheduler.hours',
        'MINUTES': 'scheduler.minutes',
        'SECONDS': 'scheduler.seconds'
    };


    vm.schedulerTimeUnitValues = Object.keys(vm.schedulerTimeUnits);

    vm.weeklyRepeats = {
      0: { label: 'scheduler.sunday-label', tooltip: 'scheduler.repeat-on-sunday' },  
      1: { label: 'scheduler.monday-label', tooltip: 'scheduler.repeat-on-monday' },
      2: { label: 'scheduler.tuesday-label', tooltip: 'scheduler.repeat-on-tuesday' },
      3: { label: 'scheduler.wednesday-label', tooltip: 'scheduler.repeat-on-wednesday' },
      4: { label: 'scheduler.thursday-label', tooltip: 'scheduler.repeat-on-thursday' },
      5: { label: 'scheduler.friday-label', tooltip: 'scheduler.repeat-on-friday' },
      6: { label: 'scheduler.saturday-label', tooltip: 'scheduler.repeat-on-saturday' },
    };

    vm.weeklyRepeatsKeys = Object.keys(vm.weeklyRepeats);

    vm.weeklyRepeatControl  = function(index) {
        return vm.addSchedulerEventFormGroup.get('weeklyRepeat').at(index);
    }

    vm.toggleWeeklyRepeat = function(key) {
        const control = vm.weeklyRepeatControl(key);
        control.setValue(!control.value);
        vm.addSchedulerEventFormGroup.get('weeklyRepeat').markAsDirty();
    }

    vm.createDefaultWeeklyRepeat = function(startDate, weeklyRepeat) {
        if (!weeklyRepeat) {
          weeklyRepeat = [];
          for (let i = 0; i < 7; i++) {
            weeklyRepeat[i] = false;
          }
        }
        if (typeof startDate !== 'undefined' && startDate !== null) {
          const setCurrentDate = weeklyRepeat.filter(repeat => repeat).length === 0;
          if (setCurrentDate) {
            const day = moment(startDate).day();
            weeklyRepeat[day] = true;
          }
        }
        return weeklyRepeat;
    }

    vm.endsOnDateValidator = function(startDate, endsOnDate) {
        return (group) => {
          if (group.controls[endsOnDate].status === 'VALID') {
            if (group.controls[startDate].value.getTime() > group.controls[endsOnDate].value.getTime()) {
              return { endsOnDateValidator: true };
            }
          }
          return null;
        };
    };

    vm.endsOnDateMatcher = {
        isErrorState: function(control) {
            const invalidCtrl = control && !!(control.invalid);
            const invalidParent = control && !!(control.parent &&
            control.parent.invalid && control.parent.hasError('endsOnDateValidator'));
            return (invalidCtrl || invalidParent);
        }
    };

    vm.datePickerOpened = function() {
      $('.mat-datetimepicker-popup').addClass('sf-scheduler-event-form');
    };


    vm.addSchedulerEventFormGroup = vm.fb.group({
      name: ['', [vm.validators.required]],
      startDate: [startDate, [vm.validators.required]],
      startTime: [startTime, [vm.validators.required]],
      condition: ['consumption', []],
      volumeInLitters: [1000, [vm.validators.required, vm.validators.min(1)]],
      durationInMinutes: [30, [vm.validators.required, vm.validators.min(1)]],
      repeat: [null, []],
      repeatType: [null, [vm.validators.required]],
      weeklyRepeat: vm.fb.array(vm.createDefaultWeeklyRepeat()),
      endsOnDate: [null, [vm.validators.required]],
      days: [null, [vm.validators.min(1), vm.validators.required]],
      weeks: [null, [vm.validators.min(1), vm.validators.required]],
      timerRepeat: vm.fb.group(
        {
          repeatInterval: [null, [vm.validators.required, vm.validators.min(0)]],
          timeUnit: [null, [vm.validators.required]]
        }
      )
    },
    {
        validator: vm.endsOnDateValidator('startDate', 'endsOnDate')
    });

    vm.addSchedulerEventFormGroup.get('repeat').valueChanges.subscribe((repeat) => {
      if (repeat) {
        vm.addSchedulerEventFormGroup.get('repeatType').patchValue('DAILY', {emitEvent: false});
        const startDate = vm.addSchedulerEventFormGroup.get('startDate').value;
        const endsOnDate = new Date(
          startDate.getFullYear(),
          startDate.getMonth(),
          startDate.getDate() + 5);
        vm.addSchedulerEventFormGroup.get('endsOnDate').patchValue(endsOnDate, {emitEvent: false});
      }
      vm.updateEnabledState();
    });

    vm.addSchedulerEventFormGroup.get('repeatType').valueChanges.subscribe((repeatType) => {
      if (repeatType === 'WEEKLY') {
          const startDate = vm.addSchedulerEventFormGroup.get('startDate').value;
          vm.addSchedulerEventFormGroup.get('weeklyRepeat').patchValue(vm.createDefaultWeeklyRepeat(startDate), {emitEvent: false});
      }
      vm.updateEnabledState();
    });

    vm.addSchedulerEventFormGroup.get('weeklyRepeat').valueChanges.subscribe((weeklyRepeat) => {
      const startDate = vm.addSchedulerEventFormGroup.get('startDate').value;
      vm.addSchedulerEventFormGroup.get('weeklyRepeat').patchValue(vm.createDefaultWeeklyRepeat(startDate, weeklyRepeat),
        {emitEvent: false});
    });

    vm.addSchedulerEventFormGroup.get('condition').valueChanges.subscribe(() => {
      vm.updateEnabledState();
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.updateEnabledState = function() {
        vm.addSchedulerEventFormGroup.enable({emitEvent: false});
        const repeat = vm.addSchedulerEventFormGroup.get('repeat').value;
        const condition = vm.addSchedulerEventFormGroup.get('condition').value;
        if (!repeat) {
            vm.addSchedulerEventFormGroup.get('repeatType').disable({emitEvent: false});
            vm.addSchedulerEventFormGroup.get('weeklyRepeat').disable({emitEvent: false});
            vm.addSchedulerEventFormGroup.get('endsOnDate').disable({emitEvent: false});
            vm.addSchedulerEventFormGroup.get('timerRepeat').disable({emitEvent: false});
            vm.addSchedulerEventFormGroup.get('days').disable({emitEvent: false});
            vm.addSchedulerEventFormGroup.get('weeks').disable({emitEvent: false});
        } else {
            const repeatType = vm.addSchedulerEventFormGroup.get('repeatType').value;
            if (repeatType !== 'WEEKLY') {
                vm.addSchedulerEventFormGroup.get('weeklyRepeat').disable({emitEvent: false});
            }
            if (repeatType !== 'TIMER') {
                vm.addSchedulerEventFormGroup.get('timerRepeat').disable({emitEvent: false});
            }
            if (repeatType !== 'EVERY_N_DAYS') {
                vm.addSchedulerEventFormGroup.get('days').disable({emitEvent: false});
            }
            if (repeatType !== 'EVERY_N_WEEKS') {
                vm.addSchedulerEventFormGroup.get('weeks').disable({emitEvent: false});
            }
        }
        if (condition === 'consumption') {
            vm.addSchedulerEventFormGroup.get('durationInMinutes').disable({emitEvent: false});
        } else {
            vm.addSchedulerEventFormGroup.get('volumeInLitters').disable({emitEvent: false});
        }

        vm.addSchedulerEventFormGroup.get('repeatType').updateValueAndValidity({emitEvent: false});
        vm.addSchedulerEventFormGroup.get('weeklyRepeat').updateValueAndValidity({emitEvent: false});
        vm.addSchedulerEventFormGroup.get('endsOnDate').updateValueAndValidity({emitEvent: false});
        vm.addSchedulerEventFormGroup.get('timerRepeat').updateValueAndValidity({emitEvent: false});
        vm.addSchedulerEventFormGroup.get('days').updateValueAndValidity({emitEvent: false});
        vm.addSchedulerEventFormGroup.get('weeks').updateValueAndValidity({emitEvent: false});
        vm.addSchedulerEventFormGroup.get('durationInMinutes').updateValueAndValidity({emitEvent: false});
        vm.addSchedulerEventFormGroup.get('volumeInLitters').updateValueAndValidity({emitEvent: false});
    };

    vm.save = function() {
        vm.addSchedulerEventFormGroup.markAsPristine();
        saveSchedulerEventObservable().subscribe(
            function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };

    vm.updateEnabledState();

    function saveSchedulerEventObservable() {
        const formValues = vm.addSchedulerEventFormGroup.value;
        let schedulerEvent = {
            name: formValues.name,
            type: 'START_IRRIGATION',
            originatorId: widgetContext.stateController.getStateParams().entityId,
            schedule: {
                timezone: moment.tz.guess()
            },
            configuration: {
                msgType: 'START_IRRIGATION',
                msgBody: {}
            }
        };
        const startDateValue = formValues.startDate;
        const startTimeValue = formValues.startTime;
        const startTime = new Date(
            startDateValue.getFullYear(),
            startDateValue.getMonth(),
            startDateValue.getDate(),
            startTimeValue.getHours(),
            startTimeValue.getMinutes(),
            startTimeValue.getSeconds()
        );
        schedulerEvent.schedule.startTime = startTime.getTime();
        if (formValues.repeat) {
            schedulerEvent.schedule.repeat = {
                type: formValues.repeatType,
                endsOn: formValues.endsOnDate.getTime()
            };
            if (formValues.repeatType === 'WEEKLY') {
                schedulerEvent.schedule.repeat.repeatOn = [];
                for (let i = 0; i < 7; i++) {
                    if (formValues.weeklyRepeat[i]) {
                        schedulerEvent.schedule.repeat.repeatOn.push(i);
                    }
                }
            } else if (formValues.repeatType === 'TIMER') {
                schedulerEvent.schedule.repeat.repeatInterval = formValues.timerRepeat.repeatInterval;
                schedulerEvent.schedule.repeat.timeUnit = formValues.timerRepeat.timeUnit;
            } else if (formValues.repeatType === 'EVERY_N_DAYS') {
                schedulerEvent.schedule.repeat.days = formValues.days;
            } else if (formValues.repeatType === 'EVERY_N_WEEKS') {
                schedulerEvent.schedule.repeat.weeks = formValues.weeks;
            }
        }
        const condition = formValues.condition;
        if (condition === 'consumption') {
            schedulerEvent.configuration.msgBody.volumeInLitters = formValues.volumeInLitters;
        } else {
            schedulerEvent.configuration.msgBody.durationInMinutes = formValues.durationInMinutes;
        }
        return schedulerEventService.saveSchedulerEvent(schedulerEvent);
    }

}

