actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let schedulerEventService = $injector.get(widgetContext.servicesMap.get('schedulerEventService'));

openEditSchedulerEventDialog();

function openEditSchedulerEventDialog() {
    customDialog.customDialog(htmlTemplate, EditSchedulerEventDialogController).subscribe();
}


function EditSchedulerEventDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;

    vm.schedulerEvent = null;

    var currentDate = new Date();
    var startDate = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      currentDate.getDate());
    var startTime = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      currentDate.getDate(),
      0,0,0,0);

    vm.schedulerRepeatTypes = {
        'DAILY': 'scheduler.daily',
        'EVERY_N_DAYS': 'scheduler.every-n-days',
        'WEEKLY': 'scheduler.weekly',
        'EVERY_N_WEEKS': 'scheduler.every-n-weeks',
        'MONTHLY': 'scheduler.monthly',
        'YEARLY': 'scheduler.yearly',
        'TIMER': 'scheduler.timer'
    };


    vm.schedulerRepeatTypeValues = Object.keys(vm.schedulerRepeatTypes);

    vm.schedulerTimeUnits = {
        'HOURS': 'scheduler.hours',
        'MINUTES': 'scheduler.minutes',
        'SECONDS': 'scheduler.seconds'
    };


    vm.schedulerTimeUnitValues = Object.keys(vm.schedulerTimeUnits);

    vm.weeklyRepeats = {
      0: { label: 'scheduler.sunday-label', tooltip: 'scheduler.repeat-on-sunday' },  
      1: { label: 'scheduler.monday-label', tooltip: 'scheduler.repeat-on-monday' },
      2: { label: 'scheduler.tuesday-label', tooltip: 'scheduler.repeat-on-tuesday' },
      3: { label: 'scheduler.wednesday-label', tooltip: 'scheduler.repeat-on-wednesday' },
      4: { label: 'scheduler.thursday-label', tooltip: 'scheduler.repeat-on-thursday' },
      5: { label: 'scheduler.friday-label', tooltip: 'scheduler.repeat-on-friday' },
      6: { label: 'scheduler.saturday-label', tooltip: 'scheduler.repeat-on-saturday' },
    };

    vm.weeklyRepeatsKeys = Object.keys(vm.weeklyRepeats);

    vm.weeklyRepeatControl  = function(index) {
        return vm.editSchedulerEventFormGroup.get('weeklyRepeat').at(index);
    }

    vm.toggleWeeklyRepeat = function(key) {
        const control = vm.weeklyRepeatControl(key);
        control.setValue(!control.value);
        vm.editSchedulerEventFormGroup.get('weeklyRepeat').markAsDirty();
    }

    vm.createDefaultWeeklyRepeat = function(startDate, weeklyRepeat) {
        if (!weeklyRepeat) {
          weeklyRepeat = [];
          for (let i = 0; i < 7; i++) {
            weeklyRepeat[i] = false;
          }
        }
        if (typeof startDate !== 'undefined' && startDate !== null) {
          const setCurrentDate = weeklyRepeat.filter(repeat => repeat).length === 0;
          if (setCurrentDate) {
            const day = moment(startDate).day();
            weeklyRepeat[day] = true;
          }
        }
        return weeklyRepeat;
    }

    vm.endsOnDateValidator = function(startDate, endsOnDate) {
        return (group) => {
          if (group.controls[endsOnDate].status === 'VALID') {
            if (group.controls[startDate].value.getTime() > group.controls[endsOnDate].value.getTime()) {
              return { endsOnDateValidator: true };
            }
          }
          return null;
        };
    };

    vm.endsOnDateMatcher = {
        isErrorState: function(control) {
            const invalidCtrl = control && !!(control.invalid);
            const invalidParent = control && !!(control.parent &&
            control.parent.invalid && control.parent.hasError('endsOnDateValidator'));
            return (invalidCtrl || invalidParent);
        }
    };

    vm.datePickerOpened = function() {
      $('.mat-datetimepicker-popup').addClass('sf-scheduler-event-form');
    };


    vm.editSchedulerEventFormGroup = vm.fb.group({
      name: ['', [vm.validators.required]],
      startDate: [startDate, [vm.validators.required]],
      startTime: [startTime, [vm.validators.required]],
      condition: ['consumption', []],
      volumeInLitters: [1000, [vm.validators.required, vm.validators.min(1)]],
      durationInMinutes: [30, [vm.validators.required, vm.validators.min(1)]],
      repeat: [null, []],
      repeatType: [null, [vm.validators.required]],
      weeklyRepeat: vm.fb.array(vm.createDefaultWeeklyRepeat()),
      endsOnDate: [null, [vm.validators.required]],
      days: [null, [vm.validators.min(1), vm.validators.required]],
      weeks: [null, [vm.validators.min(1), vm.validators.required]],
      timerRepeat: vm.fb.group(
        {
          repeatInterval: [null, [vm.validators.required, vm.validators.min(0)]],
          timeUnit: [null, [vm.validators.required]]
        }
      )
    },
    {
        validator: vm.endsOnDateValidator('startDate', 'endsOnDate')
    });

    vm.editSchedulerEventFormGroup.get('repeat').valueChanges.subscribe((repeat) => {
      if (repeat) {
        vm.editSchedulerEventFormGroup.get('repeatType').patchValue('DAILY', {emitEvent: false});
        const startDate = vm.editSchedulerEventFormGroup.get('startDate').value;
        const endsOnDate = new Date(
          startDate.getFullYear(),
          startDate.getMonth(),
          startDate.getDate() + 5);
        vm.editSchedulerEventFormGroup.get('endsOnDate').patchValue(endsOnDate, {emitEvent: false});
      }
      vm.updateEnabledState();
    });

    vm.editSchedulerEventFormGroup.get('repeatType').valueChanges.subscribe((repeatType) => {
      if (repeatType === 'WEEKLY') {
          const startDate = vm.editSchedulerEventFormGroup.get('startDate').value;
          vm.editSchedulerEventFormGroup.get('weeklyRepeat').patchValue(vm.createDefaultWeeklyRepeat(startDate), {emitEvent: false});
      }
      vm.updateEnabledState();
    });

    vm.editSchedulerEventFormGroup.get('weeklyRepeat').valueChanges.subscribe((weeklyRepeat) => {
      const startDate = vm.editSchedulerEventFormGroup.get('startDate').value;
      vm.editSchedulerEventFormGroup.get('weeklyRepeat').patchValue(vm.createDefaultWeeklyRepeat(startDate, weeklyRepeat),
        {emitEvent: false});
    });

    vm.editSchedulerEventFormGroup.get('condition').valueChanges.subscribe(() => {
      vm.updateEnabledState();
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.updateEnabledState = function() {
        vm.editSchedulerEventFormGroup.enable({emitEvent: false});
        const repeat = vm.editSchedulerEventFormGroup.get('repeat').value;
        const condition = vm.editSchedulerEventFormGroup.get('condition').value;
        if (!repeat) {
            vm.editSchedulerEventFormGroup.get('repeatType').disable({emitEvent: false});
            vm.editSchedulerEventFormGroup.get('weeklyRepeat').disable({emitEvent: false});
            vm.editSchedulerEventFormGroup.get('endsOnDate').disable({emitEvent: false});
            vm.editSchedulerEventFormGroup.get('timerRepeat').disable({emitEvent: false});
            vm.editSchedulerEventFormGroup.get('days').disable({emitEvent: false});
            vm.editSchedulerEventFormGroup.get('weeks').disable({emitEvent: false});
        } else {
            const repeatType = vm.editSchedulerEventFormGroup.get('repeatType').value;
            if (repeatType !== 'WEEKLY') {
                vm.editSchedulerEventFormGroup.get('weeklyRepeat').disable({emitEvent: false});
            }
            if (repeatType !== 'TIMER') {
                vm.editSchedulerEventFormGroup.get('timerRepeat').disable({emitEvent: false});
            }
            if (repeatType !== 'EVERY_N_DAYS') {
                vm.editSchedulerEventFormGroup.get('days').disable({emitEvent: false});
            }
            if (repeatType !== 'EVERY_N_WEEKS') {
                vm.editSchedulerEventFormGroup.get('weeks').disable({emitEvent: false});
            }
        }
        if (condition === 'consumption') {
            vm.editSchedulerEventFormGroup.get('durationInMinutes').disable({emitEvent: false});
        } else {
            vm.editSchedulerEventFormGroup.get('volumeInLitters').disable({emitEvent: false});
        }

        vm.editSchedulerEventFormGroup.get('repeatType').updateValueAndValidity({emitEvent: false});
        vm.editSchedulerEventFormGroup.get('weeklyRepeat').updateValueAndValidity({emitEvent: false});
        vm.editSchedulerEventFormGroup.get('endsOnDate').updateValueAndValidity({emitEvent: false});
        vm.editSchedulerEventFormGroup.get('timerRepeat').updateValueAndValidity({emitEvent: false});
        vm.editSchedulerEventFormGroup.get('days').updateValueAndValidity({emitEvent: false});
        vm.editSchedulerEventFormGroup.get('weeks').updateValueAndValidity({emitEvent: false});
        vm.editSchedulerEventFormGroup.get('durationInMinutes').updateValueAndValidity({emitEvent: false});
        vm.editSchedulerEventFormGroup.get('volumeInLitters').updateValueAndValidity({emitEvent: false});
    };

    vm.save = function() {
        vm.editSchedulerEventFormGroup.markAsPristine();
        saveSchedulerEventObservable().subscribe(
            function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };

    loadSchedulerEvent();

    function saveSchedulerEventObservable() {
        const formValues = vm.editSchedulerEventFormGroup.value;
        vm.schedulerEvent.name = formValues.name;
        vm.schedulerEvent.schedule = { timezone: moment.tz.guess() };
        const startDateValue = formValues.startDate;
        const startTimeValue = formValues.startTime;
        const startTime = new Date(
            startDateValue.getFullYear(),
            startDateValue.getMonth(),
            startDateValue.getDate(),
            startTimeValue.getHours(),
            startTimeValue.getMinutes(),
            startTimeValue.getSeconds()
        );
        vm.schedulerEvent.schedule.startTime = startTime.getTime();
        if (formValues.repeat) {
            vm.schedulerEvent.schedule.repeat = {
                type: formValues.repeatType,
                endsOn: formValues.endsOnDate.getTime()
            };
            if (formValues.repeatType === 'WEEKLY') {
                vm.schedulerEvent.schedule.repeat.repeatOn = [];
                for (let i = 0; i < 7; i++) {
                    if (formValues.weeklyRepeat[i]) {
                        vm.schedulerEvent.schedule.repeat.repeatOn.push(i);
                    }
                }
            } else if (formValues.repeatType === 'TIMER') {
                vm.schedulerEvent.schedule.repeat.repeatInterval = formValues.timerRepeat.repeatInterval;
                vm.schedulerEvent.schedule.repeat.timeUnit = formValues.timerRepeat.timeUnit;
            } else if (formValues.repeatType === 'EVERY_N_DAYS') {
                vm.schedulerEvent.schedule.repeat.days = formValues.days;
            } else if (formValues.repeatType === 'EVERY_N_WEEKS') {
                vm.schedulerEvent.schedule.repeat.weeks = formValues.weeks;
            }
        } else {
            delete vm.schedulerEvent.repeat;
        }
        const condition = formValues.condition;
        vm.schedulerEvent.configuration.msgBody = {};
        if (condition === 'consumption') {
            vm.schedulerEvent.configuration.msgBody.volumeInLitters = formValues.volumeInLitters;
        } else {
            vm.schedulerEvent.configuration.msgBody.durationInMinutes = formValues.durationInMinutes;
        }
        return schedulerEventService.saveSchedulerEvent(vm.schedulerEvent);
    }

    function loadSchedulerEvent() {
        schedulerEventService.getSchedulerEvent(entityId.id).subscribe(
            function (schedulerEvent) {
                vm.schedulerEvent = schedulerEvent;
                vm.editSchedulerEventFormGroup.get('name').patchValue(vm.schedulerEvent.name, {emitEvent: false});

                var startDateValue = new Date(vm.schedulerEvent.schedule.startTime);
                var startDate = new Date(
                  startDateValue.getFullYear(),
                  startDateValue.getMonth(),
                  startDateValue.getDate());
                var startTime = new Date(
                  startDateValue.getFullYear(),
                  startDateValue.getMonth(),
                  startDateValue.getDate(),
                  startDateValue.getHours(),
                  startDateValue.getMinutes(),
                  startDateValue.getSeconds()
                  ,0);
                vm.editSchedulerEventFormGroup.get('startDate').patchValue(startDate, {emitEvent: false});
                vm.editSchedulerEventFormGroup.get('startTime').patchValue(startTime, {emitEvent: false});
                if (typeof vm.schedulerEvent.configuration.msgBody.volumeInLitters !== 'undefined' && 
                    vm.schedulerEvent.configuration.msgBody.volumeInLitters !== null) {
                    vm.editSchedulerEventFormGroup.get('condition').patchValue('consumption', {emitEvent: false});
                    vm.editSchedulerEventFormGroup.get('volumeInLitters').patchValue(vm.schedulerEvent.configuration.msgBody.volumeInLitters, {emitEvent: false});
                } else {
                    vm.editSchedulerEventFormGroup.get('condition').patchValue('duration', {emitEvent: false});
                    vm.editSchedulerEventFormGroup.get('durationInMinutes').patchValue(vm.schedulerEvent.configuration.msgBody.durationInMinutes, {emitEvent: false});
                }
                if (vm.schedulerEvent.schedule.repeat) {
                    vm.editSchedulerEventFormGroup.get('repeat').patchValue(true, {emitEvent: false});
                    vm.editSchedulerEventFormGroup.get('repeatType').patchValue(vm.schedulerEvent.schedule.repeat.type, {emitEvent: false});
                    if (vm.schedulerEvent.schedule.repeat.type === 'WEEKLY' && vm.schedulerEvent.schedule.repeat.repeatOn) {
                        const weeklyRepeat = vm.createDefaultWeeklyRepeat();
                        vm.schedulerEvent.schedule.repeat.repeatOn.forEach((repeatOn) => {
                            weeklyRepeat[repeatOn] = true;
                        });
                        vm.editSchedulerEventFormGroup.get('weeklyRepeat').patchValue(weeklyRepeat, {emitEvent: false});
                    } else if (vm.schedulerEvent.schedule.repeat.type === 'TIMER') {
                        vm.editSchedulerEventFormGroup.get('timerRepeat').patchValue({
                            repeatInterval: vm.schedulerEvent.schedule.repeat.repeatInterval,
                            timeUnit: vm.schedulerEvent.schedule.repeat.timeUnit
                        }, {emitEvent: false});
                    } else if (vm.schedulerEvent.schedule.repeat.type === 'EVERY_N_DAYS') {
                        vm.editSchedulerEventFormGroup.get('days').patchValue(vm.schedulerEvent.schedule.repeat.days, {emitEvent: false});
                    } else if (vm.schedulerEvent.schedule.repeat.type === 'EVERY_N_WEEKS') {
                        vm.editSchedulerEventFormGroup.get('weeks').patchValue(vm.schedulerEvent.schedule.repeat.weeks, {emitEvent: false});
                    }
                    vm.editSchedulerEventFormGroup.get('endsOnDate').patchValue(new Date(vm.schedulerEvent.schedule.repeat.endsOn), {emitEvent: false});
                } else {
                    vm.editSchedulerEventFormGroup.get('repeat').patchValue(false, {emitEvent: false});
                }

                vm.updateEnabledState();
            }
        );
    }

}

let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));

let schedulerEventService = $injector.get(widgetContext.servicesMap.get('schedulerEventService'));

openDeleteEntityDialog();

function openDeleteEntityDialog() {
    let title = 'Delete irrigation schedule \'' +
                 entityName + '\'';
    let content = 'Are you sure you want to delete the irrigation schedule \'' + entityName + '\'?';
    dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
        function(result) {
            if (result) {
                deleteEntity();
            }
        }
    );
    $('tb-confirm-dialog').addClass('sf-scheduler-delete-dialog');
}

function deleteEntity() {
    deleteEntityObservable(entityId).subscribe(
        function success() {
            widgetContext.updateAliases();
        },
        function fail() {
            showErrorDialog();
        }
    );
}

function deleteEntityObservable(entityId) {
    return schedulerEventService.deleteSchedulerEvent(entityId.id);
}

function showErrorDialog() {
    let title = 'Error';
    let content = 'An error occurred while deleting the irrigation schedule. Please try again.';
    dialogs.alert(title, content, 'CLOSE').subscribe(
        function(result) {}
    );
}

