headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openAddFieldDialog();

function openAddFieldDialog() {
    customDialog.customDialog(htmlTemplate, AddFieldDialogController).subscribe();
}

function AddFieldDialogController(instance) {
    let vm = instance;

    vm.cropTypes = ['rice', 'corn', 'wheat', 
    'sugarcane', 'maize (corn)', 'soybean oil', 'palm oil', 'potatoes',
    'cassava', 'legume pulses', 'sunflower seed oil', 'rape and mustard oil',
    'other vegetables', 'other fruits', 'sorghum', 'millet', 'groundnuts', 'beans',
    'sweet potatoes', 'bananas', 'various nuts', 'soybeans', 'cottonseed oil',
    'groundnut oil', 'yams'];

    vm.widgetContext = widgetContext;
    vm.step = 1;
    vm.field = null;

    vm.hasPolygon = false;
    vm.polygonSubscription = null;


    vm.addFieldFormGroup = vm.fb.group({
      name: ['', [vm.validators.required]],
      label: [null],
      cropType: ['wheat', [vm.validators.required]],
      minMoistureThreshold: [25, [vm.validators.required, vm.validators.min(0), vm.validators.max(100)]],
      maxMoistureThreshold: [75, [vm.validators.required, vm.validators.min(0), vm.validators.max(100)]]
    });

    vm.cancel = function() {
        cancelPolygonSubscription();
        deleteEntityObservable().subscribe(
            function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };

    vm.next = function() {
        vm.addFieldFormGroup.markAsPristine();
        saveEntityObservable().subscribe(
            function (entity) {
                vm.field = entity;
                widgetContext.rxjs.forkJoin([
                    saveAttributes(entity.id),
                ]).subscribe(
                    function () {
                        createPolygonSubscription().subscribe(
                            function() {
                                vm.step = 2;
                            }
                        );
                    }
                );
            }
        );
    };

    vm.back = function() {
        cancelPolygonSubscription();
        vm.step = 1;
    }

    vm.submit = function() {
        cancelPolygonSubscription();
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
    }

    vm.formatMoistureThreshold = function(value) {
        return value + '%';
    }

    function createPolygonSubscription() {
        var subscriptionInfo = {
            type: 'entity',
            entityType: vm.field.id.entityType,
            entityId: vm.field.id.id,
            attributes: [{ name: 'perimeter' }]
        };
        var options = {
            callbacks: {
                onDataUpdated: function(subscription) {
                    checkHasPolygon(subscription.data);
                }
            }
        };
        return widgetContext.subscriptionApi.createSubscriptionFromInfo(
                            'latest', [ subscriptionInfo ], options, false, true).pipe(
            widgetContext.rxjs.tap(function(polygonSubscription) {
                vm.polygonSubscription = polygonSubscription;
            })
        );

    }

    function checkHasPolygon(data) {
        var datasourceData = data[0].data;
        var keyData = datasourceData[0];
        var value = keyData[1];
        if (value && Array.isArray(JSON.parse(value))) {
            vm.hasPolygon = true;
        } else {
            vm.hasPolygon = false;
        }
    }

    function cancelPolygonSubscription() {
        if (vm.polygonSubscription) {
            widgetContext.subscriptionApi.removeSubscription(vm.polygonSubscription.id);
            vm.polygonSubscription = null;
        }
    }

    function saveEntityObservable() {
        return getOrCreateSmartIrrigationAssetGroup().pipe(
          widgetContext.rxjs.switchMap((smartIrrigation) => {
            const formValues = vm.addFieldFormGroup.value;
            let field = {
                name: formValues.name,
                type: 'SI Field',
                label: formValues.label
            };
            if (vm.field) {
                field.id = vm.field.id;
            }
            return assetService.saveAsset(field, smartIrrigation.id.id);
          })
        );
    }

    function deleteEntityObservable() {
        if (vm.field) {
            return assetService.deleteAsset(vm.field.id.id);
        } else {
            return widgetContext.rxjs.of(null);
        }
    }

    function saveAttributes(entityId) {
        let cropType = vm.addFieldFormGroup.get('cropType').value;
        let minMoistureThreshold = vm.addFieldFormGroup.get('minMoistureThreshold').value;
        let maxMoistureThreshold = vm.addFieldFormGroup.get('maxMoistureThreshold').value;
        let attributesArray = [
            {key: 'cropType', value: cropType},
            {key: 'minMoistureThreshold', value: minMoistureThreshold},
            {key: 'maxMoistureThreshold', value: maxMoistureThreshold}
        ];
        return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
    }

    function getOrCreateSmartIrrigationAssetGroup() {
      return getEntityGroupByName("Smart Irrigation", "ASSET").pipe(
          widgetContext.rxjs.switchMap((group) => {
              if (group) {
                  return widgetContext.rxjs.of(group);
              } else {
                  var smartIrrigation = {
                    type: 'ASSET',
                    name: 'Smart Irrigation'
                  };
                  return entityGroupService.saveEntityGroup(smartIrrigation);
              }
          })
      );
    }

    function getEntityGroupByName(groupName, groupType) {
      var entityGroupsPageLink = widgetContext.pageLink(10, 0, groupName);
      return entityGroupService.getEntityGroupsByPageLink(entityGroupsPageLink, groupType, {ignoreLoading: true}).pipe(
          widgetContext.rxjs.map((data) => {
            if (data.data.length) {
                return data.data.find((group) => group.name === groupName);
            } else {
                return null;
            }
          })
      );
    }

}

