let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openEditFieldDialog();

function openEditFieldDialog() {
    customDialog.customDialog(htmlTemplate, EditFieldDialogController).subscribe();
}

function EditFieldDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;
    vm.entityId = entityId;

    vm.cropTypes = ['rice', 'corn', 'wheat', 
    'sugarcane', 'maize (corn)', 'soybean oil', 'palm oil', 'potatoes',
    'cassava', 'legume pulses', 'sunflower seed oil', 'rape and mustard oil',
    'other vegetables', 'other fruits', 'sorghum', 'millet', 'groundnuts', 'beans',
    'sweet potatoes', 'bananas', 'various nuts', 'soybeans', 'cottonseed oil',
    'groundnut oil', 'yams'];

    vm.widgetContext = widgetContext;
    vm.field = null;

    vm.perimeter = null;
    vm.hasPolygon = false;
    vm.polygonChanged = false;
    vm.polygonSubscription = null;


    vm.editFieldFormGroup = vm.fb.group({
      name: ['', [vm.validators.required]],
      label: [null],
      cropType: [null, [vm.validators.required]],
      minMoistureThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(100)]],
      maxMoistureThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(100)]]
    });

    vm.cancel = function() {
        cancelPolygonSubscription();
        restorePolygon().subscribe(
            function () {
                vm.dialogRef.close(null);
            }
        );
    };

    vm.save = function() {
        vm.editFieldFormGroup.markAsPristine();
        widgetContext.rxjs.forkJoin([
            saveAttributes(entityId),
            saveField()
        ]).subscribe(
            function () {
                cancelPolygonSubscription();
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };

    loadField();

    function createPolygonSubscription() {
        var subscriptionInfo = {
            type: 'entity',
            entityType: vm.field.id.entityType,
            entityId: vm.field.id.id,
            attributes: [{ name: 'perimeter' }]
        };
        var options = {
            callbacks: {
                onDataUpdated: function(subscription) {
                    checkPolygon(subscription.data);
                }
            }
        };
        return widgetContext.subscriptionApi.createSubscriptionFromInfo(
                            'latest', [ subscriptionInfo ], options, false, true).pipe(
            widgetContext.rxjs.tap(function(polygonSubscription) {
                vm.polygonSubscription = polygonSubscription;
            })
        );

    }

    function checkPolygon(data) {
        var datasourceData = data[0].data;
        var keyData = datasourceData[0];
        var value = keyData[1];
        if (value && Array.isArray(JSON.parse(value))) {
            vm.hasPolygon = true;
        } else {
            vm.hasPolygon = false;
        }
        vm.polygonChanged = vm.perimeter !== value;
    }

    function cancelPolygonSubscription() {
        if (vm.polygonSubscription) {
            widgetContext.subscriptionApi.removeSubscription(vm.polygonSubscription.id);
            vm.polygonSubscription = null;
        }
    }

    function saveField() {
        const formValues = vm.editFieldFormGroup.value;
        vm.field.name = formValues.name;
        vm.field.label = formValues.label;
        return assetService.saveAsset(vm.field);
    }

    function saveAttributes(entityId) {
        let cropType = vm.editFieldFormGroup.get('cropType').value;
        let minMoistureThreshold = vm.editFieldFormGroup.get('minMoistureThreshold').value;
        let maxMoistureThreshold = vm.editFieldFormGroup.get('maxMoistureThreshold').value;
        let attributesArray = [
            {key: 'cropType', value: cropType},
            {key: 'minMoistureThreshold', value: minMoistureThreshold},
            {key: 'maxMoistureThreshold', value: maxMoistureThreshold}
        ];
        return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
    }

    function restorePolygon() {
        if (vm.polygonChanged) {
            return attributeService.saveEntityAttributes(vm.field.id, "SERVER_SCOPE", [{key: 'perimeter', value: JSON.parse(vm.perimeter)}]);
        } else {
            return widgetContext.rxjs.of(null);
        }
    }

    function loadField() {
        widgetContext.rxjs.forkJoin([
            attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['cropType', 'minMoistureThreshold', 'maxMoistureThreshold', 'perimeter']),
            assetService.getAsset(entityId.id)
        ]).subscribe(
            function (data) {
                var attributes = data[0];
                for (var i = 0; i < attributes.length; i ++) {
                    if (attributes[i].key === 'cropType') {
                        var cropType = attributes[i].value;
                        vm.editFieldFormGroup.get('cropType').patchValue(cropType, {emitEvent: false});
                    } else if (attributes[i].key === 'minMoistureThreshold') {
                        var minMoistureThreshold = attributes[i].value;
                        vm.editFieldFormGroup.get('minMoistureThreshold').patchValue(minMoistureThreshold, {emitEvent: false});
                    } else if (attributes[i].key === 'maxMoistureThreshold') {
                        var maxMoistureThreshold = attributes[i].value;
                        vm.editFieldFormGroup.get('maxMoistureThreshold').patchValue(maxMoistureThreshold, {emitEvent: false});
                    } else if (attributes[i].key === 'perimeter') {
                        vm.perimeter = JSON.stringify(attributes[i].value);
                    }
                }
                vm.field = data[1];
                vm.editFieldFormGroup.get('name').patchValue(vm.field.name, {emitEvent: false});
                vm.editFieldFormGroup.get('label').patchValue(vm.field.label, {emitEvent: false});
                createPolygonSubscription();
            }
        );
    }

}

let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));

let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));

openDeleteEntityDialog();

function openDeleteEntityDialog() {
    let title = 'Delete field \'' +
                 entityName + '\'';
    let content = 'Are you sure you want to delete the field \'' + entityName + '\'?';
    dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
        function(result) {
            if (result) {
                deleteEntity();
            }
        }
    );
    $('tb-confirm-dialog').addClass('sf-field-delete-dialog');
}

function deleteEntity() {
    deleteEntityObservable(entityId).subscribe(
        function success() {
            widgetContext.updateAliases();
        },
        function fail() {
            showErrorDialog();
        }
    );
}

function deleteEntityObservable(entityId) {
    return assetService.deleteAsset(entityId.id);
}

function showErrorDialog() {
    let title = 'Error';
    let content = 'An error occurred while deleting the field. Please try again.';
    dialogs.alert(title, content, 'CLOSE').subscribe(
        function(result) {}
    );
}

