actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openEditSensorDialog();

function openEditSensorDialog() {
    customDialog.customDialog(htmlTemplate, EditSensorDialogController).subscribe();
}

function EditSensorDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;
    vm.entityId = entityId;

    vm.widgetContext = widgetContext;
    vm.sensor = null;

    vm.location = null;
    vm.hasLocation = false;
    vm.locationChanged = false;
    vm.locationSubscription = null;


    vm.editSensorFormGroup = vm.fb.group({
      name: ['', [vm.validators.required]],
      label: [null],
    });

    vm.cancel = function() {
        cancelLocationSubscription();
        restoreLocation().subscribe(
            function () {
                vm.dialogRef.close(null);
            }
        );
    };

    vm.save = function() {
        vm.editSensorFormGroup.markAsPristine();
        widgetContext.rxjs.forkJoin([
            saveSensor()
        ]).subscribe(
            function () {
                cancelLocationSubscription();
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };

    loadSensor();

    function createLocationSubscription() {
        var subscriptionInfo = {
            type: 'entity',
            entityType: vm.sensor.id.entityType,
            entityId: vm.sensor.id.id,
            attributes: [{ name: 'latitude' }, {name: 'longitude'}]
        };
        var options = {
            callbacks: {
                onDataUpdated: function(subscription) {
                    checkLocation(subscription.data);
                }
            }
        };
        return widgetContext.subscriptionApi.createSubscriptionFromInfo(
                            'latest', [ subscriptionInfo ], options, false, true).pipe(
            widgetContext.rxjs.tap(function(locationSubscription) {
                vm.locationSubscription = locationSubscription;
            })
        );

    }

    function checkLocation(data) {
        var hasLocation = false;
        var latitude, longitude;
        if (data.length === 2) {
            for (var i = 0; i < data.length; i ++) {
                var datasourceData = data[i];
                if (datasourceData.dataKey.name === 'latitude') {
                    latitude = datasourceData.data[0][1];
                } else if (datasourceData.dataKey.name === 'longitude') {
                    longitude = datasourceData.data[0][1];
                }
            }
            if (isNumeric(latitude) && isNumeric(longitude)) {
                hasLocation = true;
            }
        }
        var locationChanged = false;
        if (hasLocation && !vm.location || !hasLocation && vm.location || 
            vm.location[0] !== latitude || vm.location[1] !== longitude) {
            locationChanged = true;
        }
        vm.hasLocation = hasLocation;
        vm.locationChanged = locationChanged;
    }

    function isNumeric(val) {
        if (typeof val === "number") {
            return true;
        } else if (typeof val === "string") {
            return !isNaN(val) && 
                !isNaN(parseFloat(val));
        } else {
            return false;
        }
    }

    function cancelLocationSubscription() {
        if (vm.locationSubscription) {
            widgetContext.subscriptionApi.removeSubscription(vm.locationSubscription.id);
            vm.locationSubscription = null;
        }
    }

    function saveSensor() {
        const formValues = vm.editSensorFormGroup.value;
        vm.sensor.name = formValues.name;
        vm.sensor.label = formValues.label;
        return deviceService.saveDevice(vm.sensor);
    }

    function restoreLocation() {
        if (vm.locationChanged) {
            var latitude = null;
            var longitude = null;
            if (vm.location && vm.location.length === 2) {
                latitude = vm.location[0];
                longitude = vm.location[1];
            }
            return attributeService.saveEntityAttributes(vm.sensor.id, "SERVER_SCOPE",
                [
                    { key: 'latitude', value: latitude },
                    { key: 'longitude', value: longitude }
                ]
            );
        } else {
            return widgetContext.rxjs.of(null);
        }
    }

    function loadSensor() {
        widgetContext.rxjs.forkJoin([
            attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['latitude', 'longitude']),
            deviceService.getDevice(entityId.id)
        ]).subscribe(
            function (data) {
                var attributes = data[0];
                for (var i = 0; i < attributes.length; i ++) {
                    if (attributes[i].key === 'latitude' || attributes[i].key === 'longitude') {
                        if (!vm.location) {
                            vm.location = [];
                        }
                        vm.location[attributes[i].key === 'latitude' ? 0 : 1] = attributes[i].value;
                    }
                }
                vm.sensor = data[1];
                vm.editSensorFormGroup.get('name').patchValue(vm.sensor.name, {emitEvent: false});
                vm.editSensorFormGroup.get('label').patchValue(vm.sensor.label, {emitEvent: false});
                createLocationSubscription();
            }
        );
    }

}

let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));

let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));

openDeleteEntityDialog();

function openDeleteEntityDialog() {
    let title = 'Delete sensor \'' +
                 entityName + '\'';
    let content = 'Are you sure you want to delete the sensor \'' + entityName + '\'?';
    dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
        function(result) {
            if (result) {
                deleteEntity();
            }
        }
    );
    $('tb-confirm-dialog').addClass('sf-sensor-delete-dialog');
}

function deleteEntity() {
    deleteEntityObservable(entityId).subscribe(
        function success() {
            widgetContext.updateAliases();
        },
        function fail() {
            showErrorDialog();
        }
    );
}

function deleteEntityObservable(entityId) {
    return deviceService.deleteDevice(entityId.id);
}

function showErrorDialog() {
    let title = 'Error';
    let content = 'An error occurred while deleting the sensor. Please try again.';
    dialogs.alert(title, content, 'CLOSE').subscribe(
        function(result) {}
    );
}

settings.markerImageFunction:
var type = data['type'];
if (type === 'SI Soil Moisture Sensor') {
    var text = getSensorText();
    var defaultMarkerWidth = 20;
    var width = defaultMarkerWidth + getTextWidth(text);
    var svgStr = getSensorSvg(width, text);
    var encodedSvg = encodeURIComponent(svgStr);
    var svgUrl = 'data:image/svg+xml,' + encodedSvg;
    return {
        url: svgUrl,
        size: width,
        markerOffset: [defaultMarkerWidth / 2, defaultMarkerWidth / 2],
        tooltipOffset: [width * 0.5 - defaultMarkerWidth / 2, - (defaultMarkerWidth / 2)]
    };
} else {
    return null;
}

function getSensorSvg(width, text) {
    var color = '#FFFFFF';
    var background = getSensorBackground(color, width);
    var svgWidth = width;
    var svgHeight = 20;
    var opacity = 1;
    var sensorSvg = '<svg opacity="' + opacity + '" width="' + svgWidth + '" height="' + svgHeight + '" viewBox="0 0 ' + svgWidth + ' ' + svgHeight + '" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                    background;
    if (text !== '') {
        var transform = '8 0';
        sensorSvg += '<text transform="translate(' + transform + ')" fill="black" xml:space="preserve" style="white-space: pre" font-family="Roboto" font-size="12" font-weight="600" letter-spacing="0em"><tspan x="0.10664" y="14.2637">' + text + '</tspan></text>';
    }
    sensorSvg += '</svg>';
    return sensorSvg;    
}

function getSensorBackground(color, width) {
     if (width === 20) {
         return '<circle cx="10" cy="10" r="10" fill="'+ color +'"/>';
     } else {
         return '<circle cx="10" cy="10" r="10" fill="'+ color +'"/>' + 
                '<circle cx="' + (width - 35) + '" cy="10" r="10" fill="'+ color +'"/>' +
                '<rect x="10" y="0" width="' + (width - 45) + '" height="20" rx="1" fill="'+ color +'"/>';
     }
}

function getSensorText() {
    if (data['moisture']) {
        var moisture = Number(data['moisture']);
        return moisture + '%';
    } else {
        return '';
    }
}

function getTextWidth(text) {
    var width = 0;
    for (var i = 0; i < text.length; i++) {
        var charWidth;
        var character = text.charAt(i);
        if (character === ' ') {
            charWidth = 8;
        } else if (character === '-') {
            charWidth = 7;
        } else if (character === '°') {
            charWidth = 5;
        } else if (!isNaN(character * 1)){
            charWidth = 9;
        } else {
            // Upper case
            if (character === character.toUpperCase()) {
                charWidth = 12;
            // Lower case    
            } else {
                charWidth = 10;
            }
        }
        width += charWidth;
    }
    return width;
}

var type = data['type'];
if (type === 'SI Soil Moisture Sensor') {
    var text = getSensorText();
    var defaultMarkerWidth = 20;
    var width = defaultMarkerWidth + getTextWidth(text);
    var svgStr = getSensorSvg(width, text);
    var encodedSvg = encodeURIComponent(svgStr);
    var svgUrl = 'data:image/svg+xml,' + encodedSvg;
    return {
        url: svgUrl,
        size: width,
        markerOffset: [defaultMarkerWidth / 2, defaultMarkerWidth / 2],
        tooltipOffset: [width * 0.5 - defaultMarkerWidth / 2, - (defaultMarkerWidth / 2)]
    };
} else {
    return null;
}

function getSensorSvg(width, text) {
    var moisture = data['moisture'];
    var minMoistureThreshold = data['minMoistureThreshold'];
    var maxMoistureThreshold = data['maxMoistureThreshold'];
    var color = '#FFFFFF';
    var textColor = '#000000';
    if (typeof moisture === 'number' && (moisture < minMoistureThreshold || moisture > maxMoistureThreshold)) {
        color = '#D12730';
        textColor = '#FFFFFF';
    }
    var background = getSensorBackground(color, width);
    var svgWidth = width;
    var svgHeight = 20;
    var opacity = 1;
    var sensorSvg = '<svg opacity="' + opacity + '" width="' + svgWidth + '" height="' + svgHeight + '" viewBox="0 0 ' + svgWidth + ' ' + svgHeight + '" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                    background;
    if (text !== '') {
        var transform = '8 0';
        sensorSvg += '<text transform="translate(' + transform + ')" fill="'+textColor+'" xml:space="preserve" style="white-space: pre" font-family="Roboto" font-size="12" font-weight="600" letter-spacing="0em"><tspan x="0.10664" y="14.2637">' + text + '</tspan></text>';
    }
    sensorSvg += '</svg>';
    return sensorSvg;    
}

function getSensorBackground(color, width) {
     if (width === 20) {
         return '<circle cx="10" cy="10" r="10" fill="'+ color +'"/>';
     } else {
         return '<circle cx="10" cy="10" r="10" fill="'+ color +'"/>' + 
                '<circle cx="' + (width - 35) + '" cy="10" r="10" fill="'+ color +'"/>' +
                '<rect x="10" y="0" width="' + (width - 45) + '" height="20" rx="1" fill="'+ color +'"/>';
     }
}

function getSensorText() {
    if (data['moisture']) {
        var moisture = Number(data['moisture']).toFixed(1);
        return moisture + '%';
    } else {
        return '';
    }
}

function getTextWidth(text) {
    var width = 0;
    for (var i = 0; i < text.length; i++) {
        var charWidth;
        var character = text.charAt(i);
        if (character === ' ') {
            charWidth = 8;
        } else if (character === '-') {
            charWidth = 7;
        } else if (character === '°') {
            charWidth = 5;
        } else if (!isNaN(character * 1)){
            charWidth = 9;
        } else {
            // Upper case
            if (character === character.toUpperCase()) {
                charWidth = 12;
            // Lower case    
            } else {
                charWidth = 10;
            }
        }
        width += charWidth;
    }
    return width;
}

