var statusClass = data[0]['active'] === "Active" ? "active" : "inactive";
var moistureTs = data[0]['moisture|ts'];
var label = data[0]['label'];
var moistureElapsedTsHtml;
if (moistureTs) {
    var moistureElapsedTs = new Date().getTime() - moistureTs;
    var MINUTE = 60 * 1000;
    var HOUR = 60 * MINUTE;
    var DAY = 24 * HOUR;
    if (moistureElapsedTs > DAY) {
        moistureElapsedTs = Math.floor(moistureElapsedTs / DAY) * DAY;
    } else if (moistureElapsedTs > HOUR) {
        moistureElapsedTs = Math.floor(moistureElapsedTs / HOUR) * HOUR;
    } else if (moistureElapsedTs > MINUTE) {
        moistureElapsedTs = Math.floor(moistureElapsedTs / MINUTE) * MINUTE;
    }
    moistureElapsedTsHtml = '{{ ' + moistureElapsedTs + ' | milliSecondsToTimeString }} ago';
} else {
    moistureElapsedTsHtml = 'N/A';
}

return '<div class="sensor-details">' +
    '<div class="header">' + 
        '<h3 class="title">${entityName}</h3>' + 
        (label ? '<span class="sensor-label">' + label + '</span>' : '') + 
