    } catch (e){}
}
if (data) {
    var dateFormat;
    if (new Date().getYear() === new Date(data.startTs).getYear()) {
        dateFormat = 'd MMM';
    } else {
        dateFormat = 'd MMM yyyy';
    }
    return ctx.date.transform(data.startTs, dateFormat);
} else {
    return '';
}

