    return '<span style="font-weight: 600; background: rgba(111, 116, 242, 0.08); border-radius: 16px; padding: 4px 8px; color: #5553AF;">'+time+'</span>';
} else {
    return '';
}

var data;
if (value) {
    try {
        data = JSON.parse(value);
    } catch (e){}
}
if (data) {
    var durationThreshold = data.durationThreshold;
    var consumptionThreshold = data.consumptionThreshold;
    var text = 'N/A';
    if (durationThreshold) {
        text = (durationThreshold / 60000).toFixed(0) + ' min';
    } else if (consumptionThreshold) {
        text = consumptionThreshold.toFixed(0) + ' l';
    }
    return '<span style="font-weight: 600; background: rgba(111, 116, 242, 0.08); border-radius: 16px; padding: 4px 8px;  color: #5553AF;">'+text+'</span>';
} else {
    return '';
}

var data;
if (value) {
    try {
        data = JSON.parse(value);
    } catch (e){}
}
if (data) {
    var duration = data.duration;
    if (typeof duration === 'number') {
        return '~' + (duration / 60000).toFixed(0) + ' min';
    } else {
        return 'N/A'
    }
} else {
    return 'N/A';
}

var data;
if (value) {
    try {
        data = JSON.parse(value);
    } catch (e){}
}
if (data) {
    var consumption = data.consumption;
    if (typeof consumption === 'number') {
        return consumption.toFixed(0) + ' l';
    } else {
        return 'N/A'
    }
} else {
    return 'N/A';
}

var data;
if (value) {
    try {
        data = JSON.parse(value);
    } catch (e){}
}
if (data) {
    var durationThreshold = data.durationThreshold;
    var consumptionThreshold = data.consumptionThreshold;
    var progress = 0;
    if (durationThreshold) {
        progress = data.duration ? (data.duration / durationThreshold * 100) : 0;
    } else if (consumptionThreshold) {
        progress = data.consumption ? (data.consumption / consumptionThreshold * 100) : 0;
    }
    var percent = progress + '%';
    var percentText = progress.toFixed(0) + '%';
    var background = '111, 116, 242';
    var textColor = '#5553AF';
    return '<div style="width: 100%; height: 16px; background: linear-gradient(to right, rgba('+background+', 0.16) '+percent+', rgba('+background+', 0.08) '+percent+'); border-radius: 5.21053px;"><span style="padding-left: 4px; font-weight: 600; font-size: 13px; color: '+textColor+';">'+percentText+'</span></div>';
} else {
    return '';
}

