elementClick.customFunction:
console.log($event,entityId,entityName,additionalParams,widgetContext)

let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));

    openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

//Get value materialeScavo
var select = document.getElementById("materialeScavo");
var options = ["terra", "argilla", "roccia", "sabbia"];
for(var i=0; i<options.length; i++) {
    var opt = options[i];
    var el = document.createElement("mat-option");
    el.text = opt;
    el.value = opt;
    select.add(el);
}




function AddEntityDialogController(instance) {
    let vm = instance;
    vm.addEntityFormGroup = vm.fb.group({
     IdScavoNuovo: [null, [vm.validators.required]],
     DescrScavo: [null, [vm.validators.required]],
     materialeScavo:[null, [vm.validators.required]],
     UbicaziScavo:[null, [vm.validators.required]]
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };
    vm.save = function() {
        var formData=vm.addEntityFormGroup.value;
        console.log("formData: ",formData);
        vm.addEntityFormGroup.markAsPristine();
        var date=Date.now();
        var telData={"ts":date, "values":{"IdScavo":formData.IdScavoNuovo, "IdScavoNuovo":formData.IdScavoNuovo, "DescrScavo":formData.DescrScavo, "UbicaziScavo":formData.UbicaziScavo, "materialeScavo":formData.materialeScavo}};
        var rpc={"method":"setValue","params":{"IdScavoNuovo":formData.IdScavoNuovo,"FrqCampione":5, "DescrScavo":formData.DescrScavo, "UbicaziScavo":formData.UbicaziScavo, "materialeScavo":formData.materialeScavo}};
        widgetContext.http.post('/api/plugins/telemetry/'+entityId.entityType+'/'+entityId.id+'/timeseries/ANY?scope=ANY', telData).subscribe(data =>
        {
            vm.dialogRef.close(null);
        },function(error){
            console.log(error)
        })
        widgetContext.http.post('/api/rpc/oneway/'+entityId.id, rpc).subscribe(data =>
        {
            vm.dialogRef.close(null);
        },function(error){
            console.log(error)
        })
    };

}

