settings.cardCss:
.card {
    width: 100%;
    height: 100%;
    border-top: 1px solid #E0E0E0;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
}

.card .header {
  font-size: 1.15em;
  font-weight: 600;
  padding: 10px 10px 5px 10px;
  border-bottom: 1px solid #E0E0E0
}

.card .content {
    display: flex;
    flex-direction: column;
    align-items: stretch;
    justify-content: flex-start;
    height: 100%;
    overflow-y: auto;
    box-sizing: border-box;
}

.card .meter-photo {
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    z-index: 0;
}

.card .meter-photo img {
    width: auto;
    height: 100%;
    object-fit: contain;
}

@media (min-width: 960px) and (max-width: 1200px) {
    .card .header {
        font-size: 0.9em;
    }
}

