settings.tooltipFunction:
var darkMode = $('.tb-dashboard-page').hasClass('dark');
var content = (darkMode ? ('<div style="font-family: Roboto;">' +
                 '<div style="font-size: 14px; font-weight: 500; padding-bottom: 8px; color:#212121;">${Name}</div>') : ('<div style="font-family: Roboto;">' +
                 '<div style="font-size: 14px; font-weight: 500; padding-bottom: 8px;">${Name}</div>'));
                 
var deviceType = data['Type'];
var water;
if(data['water']==false){water="Nessuna perdita";}else{water="Perdita rilevata";}

if(darkMode==true){
    if (typeof deviceType !== undefined) {
        content += '<div style="font-size: 12px; font-weight: normal; color:#212121;">' + getInfoByDeviceType(deviceType) + '</div>';
    }
}else{
    if (typeof deviceType !== undefined) {
        content += '<div style="font-size: 12px; font-weight: normal; color:#212121;">' + getInfoByDeviceType(deviceType) + '</div>';
    }
}

content += '</div>';
return content;

function getInfoByDeviceType(type) {
    switch (type) {
        case 'AA Sensor':
            return 'Temperatura: ${temperature:1} °C<br>Umidità: ${humidity:1} %';
        case 'shelly3em':
            return '${Phase_A_Name}: ${Phase_A_power} KWh.<br>${Phase_B_Name}: ${Phase_B_power} KWh<br>${Phase_C_Name}: ${Phase_C_power} KWh';
        case 'Caldaia':
            return 'Temperatura: ${temperature:1} °C<br>Temperatura Impostata: ${setTemperature:1} °C<br>Stato: ${status}';
        case 'Chiller':
        case 'Freezer':    
            return 'Temperature: ${temperature:1} °C';
        case 'Smart Bin':
            return 'Fullness: ${level:0}%';
        case 'Liquid Level Sensor':
            return 'Liquid level: ${level:0}%';
        case 'Liquid Level Sensor':
            return 'Perdita: ${water}';
        case 'Door Sensor':
            var open = data['open'] === 'true';
            return 'State: ' + (open ? 'Open' : 'Closed');
        case 'Motion Sensor':
            var movement = data['movement'] === 'true';
            return 'Movement detection: ' + (movement ? 'Detected' : 'None');
        case 'Occupancy Sensor':
            var occupied = data['occupied'] === 'true';
            return 'State: ' + (occupied ? 'Occupied' : 'Vacant');
        case 'Smoke Sensor':
            var alarm = data['alarm'] === 'true';
            return 'Fire alarm: ' + (alarm ? 'Alarm' : 'None');
        case 'Camera':
            return 'Persone: ${person}';
        case 'Acqua':
            return water;
        default:
            return 'TODO';
    }
}

markerClick.customFunction:
