settings.markerImageFunction:
var state = data['state'];
var selectedSupermarket = data['selectedSupermarket'] === true;
var selectedMode = dsData.filter(function (data) { return data['selectedSupermarket'] === true; }).length > 0;
console.log(data);
var status = state;
var width = 30;
var svgStr = getSupermarketSvg(status, width, selectedSupermarket);
var encodedSvg = encodeURIComponent(svgStr);
var svgUrl = 'data:image/svg+xml,' + encodedSvg;

if (selectedSupermarket) {
    width += 8;
}

return {
    url: svgUrl,
    size: width,
    markerOffset: [width / 2, width / 2],
    tooltipOffset: [width * 0.5 - width / 2, - (width / 2)]
};

function getSupermarketSvg(status, width, isSelected) {
    var shape = '<svg viewBox="0 0 640 512" fill="white" height="20px" width="20px" x="5" y="5" xmlns="http://www.w3.org/2000/svg"><path d="M640 240v240c0 17.67-14.33 32-32 32H32c-17.67 0-32-14.33-32-32L0 144c0-26.51 21.49-48 48-48H64V24.01c0-13.25 10.75-23.1 24-23.1S112 10.75 112 24.01v72h64V24.01c0-13.25 10.75-23.1 24-23.1S224 10.75 224 24.01v71.1h64V48.01c0-26.51 21.49-48 48-48l96 .0049c26.51 0 48 21.49 48 48v143.1h112C618.5 192 640 213.5 640 240zM128 172c0-6.625-5.375-12-12-12h-40c-6.625 0-12 5.375-12 12v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V172zM128 268c0-6.625-5.375-12-12-12h-40c-6.625 0-12 5.375-12 12v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V268zM128 364c0-6.625-5.375-12-12-12h-40c-6.625 0-12 5.375-12 12v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V364zM256 172c0-6.625-5.375-12-12-12h-40c-6.625 0-12 5.375-12 12v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V172zM256 268c0-6.625-5.375-12-12-12h-40C197.4 256 192 261.4 192 268v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V268zM256 364c0-6.625-5.375-12-12-12h-40c-6.625 0-12 5.38-12 12v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V364zM416 76.01c0-6.625-5.375-12-12-12h-40c-6.625 0-12 5.375-12 12v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V76.01zM416 172c0-6.625-5.375-12-12-12h-40c-6.625 0-12 5.38-12 12v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V172zM416 268c0-6.625-5.375-12-12-12h-40c-6.625 0-12 5.38-12 12v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V268zM576 268c0-6.625-5.375-12-12-12h-40c-6.625 0-12 5.375-12 12v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V268zM576 364c0-6.625-5.375-12-12-12h-40c-6.625 0-12 5.375-12 12v40c0 6.625 5.375 12 12 12h40c6.625 0 12-5.375 12-12V364z"/></svg>';
    var color = getBackgroundColor(status);
    var background = getBackground(color);
    var svgWidth = isSelected ? (width + 8) : width;
    var svgHeight = isSelected ? 38 : 30;

    var opacity = selectedMode && !isSelected ? 0.4 : 1;

    var supermarketSvg = '<svg opacity="' + opacity + '" width="' + svgWidth + '" height="' + svgHeight + '" viewBox="0 0 ' + svgWidth + ' ' + svgHeight + '" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                    wrapIfSelected(background + shape, isSelected);
    if (isSelected) {
        supermarketSvg += getSupermarketSelectionStroke();
    }
    supermarketSvg += '</svg>';
    return supermarketSvg;    
}

function wrapIfSelected(svg, isSelected) {
    if (isSelected) {
        return '<g transform="translate(4 4)">' + svg + '</g>';
    } else {
        return svg;
    }
}

function getBackground(color) {
     return '<circle cx="15" cy="15" r="15" fill="'+ color +'"/>';
}

function getSupermarketSelectionStroke() {
     return '<circle cx="19" cy="19" r="18" stroke="#D5C21B" stroke-width="2"/>';
}

function getBackgroundColor(status) {
    switch(status) {
        case 'major':
            return '#D5C21B';
        case 'critical':   
            return '#E64A19';
        case 'normal':
        default:    
            return '#1F8B4D';
    }
}

