settings.cellContentFunction:
var state = value;

var stateSvg = getStateSvg(state);
var encodedStateSvg = encodeURIComponent(stateSvg);
var stateSvgUrl = 'data:image/svg+xml,' + encodedStateSvg;
var stateLabel = getStateLabel(state);

return '<span>'+
           '<img style="vertical-align: middle;" class="mat-icon" src="'+ stateSvgUrl +'">' +
           '<span style="vertical-align: middle; padding-left: 8px;">'+ stateLabel +'</span>' +
       '</span>';

function getStateSvg(state) {
    switch (state) {
        case 'critical':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#E64A19"/>' +
                   '</svg>';
        case 'major':            
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#D5C21B"/>' +
                   '</svg>';
        case 'normal':            
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#1F8B4D"/>' +
                   '</svg>';
        default:
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#AAAAAA"/>' +
                   '</svg>';
    }
}

function getStateLabel(state) {
    switch(state) {
        case 'critical':
            return 'Critical';
        case 'major':
            return 'Major';
        case 'normal':
            return 'Normal';
        default:
            return state;
    }
}

