    address = data[0]['address'];
    state = data[0]['state'];
} else {
    name = 'N/A';
    address = 'N/A';
    state = 'N/A';
}

var typeSvg = '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                '<path d="m13.636 10.8c0.59978 0 1.1276-0.32788 1.3995-0.82369l2.8629-5.1901c0.29589-0.5278-0.087968-1.1836-0.69574-1.1836h-11.836l-0.75172-1.5994h-2.615v1.5994h1.5994l2.8789' + 
                ' 6.0697-1.0796 1.9513c-0.58378 1.0716 0.18393 2.3751 1.3995 2.3751h9.5964v-1.5994h-9.5964l0.87967-1.5994zm-7.5092-5.5979h9.7164l-2.2072 3.9985h-5.6139zm0.67175 9.5964c-0.87967' + 
                ' 0-1.5914 0.71973-1.5914 1.5994s0.71174 1.5994 1.5914 1.5994 1.5994-0.71973 1.5994-1.5994-0.71973-1.5994-1.5994-1.5994zm7.997 0c-0.87967 0-1.5914 0.71973-1.5914 1.5994s0.71174' + 
                ' 1.5994 1.5914 1.5994 1.5994-0.71973 1.5994-1.5994-0.71973-1.5994-1.5994-1.5994z" stroke-width=".7997" fill="#212121"/>' + 
              '</svg>';
var encodedTypeSvg = encodeURIComponent(typeSvg);
var typeSvgUrl = 'data:image/svg+xml,' + encodedTypeSvg;

var header = '<div class="header" fxLayout="row" fxLayoutAlign="start stretch" fxLayoutGap="8px">' +
                '<div fxLayout="column" fxLayoutAlign="center"><button id="go-back" matTooltip="Go back" type="button" mat-icon-button><mat-icon>arrow_back</mat-icon></button></div>' +
                '<div fxLayout="column" fxLayoutAlign="center"><img style="vertical-align: middle;" class="title-icon mat-icon" src="'+ typeSvgUrl +'"></div>' +
                '<div fxFlex fxLayout="column" fxLayoutAlign="center">' +
                   '<div class="title">' + name + '</div>' +
                '</div>' + '<tb-dashboard-state class="alarm-badges" *ngIf="ctx.stateController.getStateParams().selectedSupermarket" [ctx]="ctx" [syncParentStateParams]="true" stateId="alarm_badges_without_report"></tb-dashboard-state>'+
             '</div>';

return '<div class="main-layout" style="width: 100%; height: 100%;" fxLayout="column">' +
          header +
          '<div fxLayout="row" class="params">' +
              '<div fxFlex="70" class="param-cell" fxLayout="column" fxLayoutGap="8px">' +
                '<div class="param-title">Address: </div>' +
                '<div class="param">'+ address +'</div>' +
              '</div>' +
              '<div fxFlex="30" class="param-cell" fxLayout="column" fxLayoutGap="8px">' +
                '<div class="param-title">State: </div>' +
                '<div class="param">'+ getStateHtml(state) +'</div>' +
              '</div>' +
          '</div>' +
       '<div>';


function getStateHtml(state) {
    var stateSvg = getStateSvg(state);
    var encodedStateSvg = encodeURIComponent(stateSvg);
    var stateSvgUrl = 'data:image/svg+xml,' + encodedStateSvg;
    var stateLabel = getStateLabel(state);
    return '<span>'+
           '<img style="vertical-align: middle;" class="mat-icon" src="'+ stateSvgUrl +'">' +
           '<span style="vertical-align: middle; padding-left: 8px;">'+ stateLabel +'</span>' +
    '</span>';
}

function getStateSvg(state) {
    switch (state) {
        case 'critical':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#E64A19"/>' +
                   '</svg>';
        case 'major':            
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#D5C21B"/>' +
                   '</svg>';
        case 'normal':            
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#1F8B4D"/>' +
                   '</svg>';
        default:
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#AAAAAA"/>' +
                   '</svg>';
    }
}

function getStateLabel(state) {
    switch(state) {
        case 'critical':
            return 'Critical';
        case 'major':
            return 'Major';
        case 'normal':
            return 'Normal';
        default:
            return state;
    }
}

