settings.tooltipFunction:
var darkMode = $('.tb-dashboard-page').hasClass('dark');
var content = (darkMode ? ('<div style="font-family: Roboto;">' +
                 '<div style="font-size: 14px; font-weight: 500; padding-bottom: 8px; color:#212121;">${Name:0}</div>') : ('<div style="font-family: Roboto;">' +
                 '<div style="font-size: 14px; font-weight: 500; padding-bottom: 8px;">${Name:0}</div>'));

if(darkMode==true){
    content += '<div style="font-size: 12px; font-weight: normal; color:#212121;">' + getInfoByDeviceType() + '</div>';
}else{
    content += '<div style="font-size: 12px; font-weight: normal; color:#212121;">' + getInfoByDeviceType() + '</div>';
}

content += '</div>';
return content;

function getInfoByDeviceType() {
    return 'Stato: ${stato}<br>Presenza: ${presenza}';
}

