var type,entityType;
if (data && data.length) {
    type = data[0]['Type'];
    entityType=data[0]['entityType'];
} else {
    type = 'N/A';
    entityType = 'N/A';
}

var detailsStateName = getDetailsStateName(type,entityType);

return '<div style="width: 100%; height: 100%;" fxLayout="column">' + 
           '<tb-dashboard-state fxFlex [ctx]="ctx" [syncParentStateParams]="true" stateId="' + detailsStateName + '"></tb-dashboard-state>' +
       '</div>';

function getDetailsStateName(deviceType,entityType) {
    if(entityType=="ASSET"){
        switch(deviceType) {
            case 'AA Energy':
                return 'energy_sensor_details';
            case 'Presenza':
                return 'presenza_details';
            case 'Affluenza':
                return 'affluenza_details';
            case 'Stato Bagno':
                return 'stato_bagno_details';
            case 'Acqua':
                return 'acqua_details';
            default:
                return 'unknown_details';
        }
    }else{
        switch(deviceType) {
        case 'Energy':
            return 'energy_multiple_sensor_details';
        case 'AA Sensor':
            return 'aa_sensor_details';
        case 'Acqua':
            return 'acqua_details';
        case 'Affluenza':
                return 'affluenza_details';
        case 'Stato Bagno':
            return 'stato_bagno_details';
        case 'AA Energy':
            return 'energy_multiple_sensor_details';
        case 'Camera':
            return 'camera_sensor_details';
        case 'Presenza':
            return 'presenza_details';
        default:
            return 'unknown_details';
        }
    }
}

