dataKeys.postFuncBody:
var battery_level;
var mvolts=value;
if (mvolts >= 3000)
{
	battery_level = 100;
}
else if (mvolts > 2900)
{
	battery_level = 100 - ((3000 - mvolts) * 58) / 100;
}
else if (mvolts > 2740)
{
	battery_level = 42 - ((2900 - mvolts) * 24) / 160;
}
else if (mvolts > 2440)
{
	battery_level = 18 - ((2740 - mvolts) * 12) / 300;
}
else if (mvolts > 2100)
{
	battery_level = 6 - ((2440 - mvolts) * 6) / 340;
}
else
{
	battery_level = 0;
}
return battery_level;
//return (((value-1750)/3700)*100).toFixed(2);

