    if(metadata.ss_orari_operativi!=null){orari_operativi=JSON.parse(metadata.ss_orari_operativi);}
    if(metadata.ss_orari_pranzo!=null){orari_pranzo=JSON.parse(metadata.ss_orari_pranzo);}
    if(valore()!="error"){
        if(orari_operativi!=null && orari_operativi.lunven!=null){
            op=operativo(orari_operativi.lunven,hour,minute);
        }else if(orari_operativi!=null){
            if(day=="lunedi"){if(orari_operativi.lunedi!=null){op=operativo(orari_operativi.lunedi,hour,minute)}}
            if(day=="martedi"){if(orari_operativi.martedi!=null){op=operativo(orari_operativi.martedi,hour,minute)}}
            if(day=="mercoledi"){if(orari_operativi.mercoledi!=null){op=operativo(orari_operativi.mercoledi,hour,minute)}}
            if(day=="giovedi"){if(orari_operativi.giovedi!=null){op=operativo(orari_operativi.giovedi,hour,minute)}}
            if(day=="venerdi"){if(orari_operativi.venerdi!=null){op=operativo(orari_operativi.venerdi,hour,minute)}}
            if(day=="sabato"){if(orari_operativi.sabato!=null){op=operativo(orari_operativi.sabato,hour,minute)}}
            if(day=="domenica"){if(orari_operativi.domenica!=null){op=operativo(orari_operativi.domenica,hour,minute)}}
        }
        if(orari_pranzo!=null && op==0){
            if(orari_pranzo.lunven!=null){
                pranzo=operativo(orari_pranzo.lunven,hour,minute);
            }else{
                if(day=="lunedi"){if(orari_pranzo.lunedi!=null){pranzo=operativo(orari_pranzo.lunedi,hour,minute)}}
                if(day=="martedi"){if(orari_pranzo.martedi!=null){pranzo=operativo(orari_pranzo.martedi,hour,minute)}}
                if(day=="mercoledi"){if(orari_pranzo.mercoledi!=null){pranzo=operativo(orari_pranzo.mercoledi,hour,minute)}}
                if(day=="giovedi"){if(orari_pranzo.giovedi!=null){pranzo=operativo(orari_pranzo.giovedi,hour,minute)}}
                if(day=="venerdi"){if(orari_pranzo.venerdi!=null){pranzo=operativo(orari_pranzo.venerdi,hour,minute)}}
                if(day=="sabato"){if(orari_pranzo.sabato!=null){pranzo=operativo(orari_pranzo.sabato,hour,minute)}}
                if(day=="domenica"){if(orari_pranzo.domenica!=null){pranzo=operativo(orari_pranzo.domenica,hour,minute)}}
            }
        }
        if(op!=0){
            msg.operativo=valore();
        }else if(pranzo!=0){
            msg.pranzo=valore();
        }else{
            msg.non_operativo=valore();
        }
    }
}

return {msg: msg, metadata: metadata, msgType: msgType};

function operativo(array,hour,minute){
    var result=1;
    var currtime = hour * 100 + minute;
    for(var i=0;i<array.length;i+=2){
        var t=i+1;
        var te=i+2;
        var temp=array[i].split(":");
        var temp2=array[t].split(":");
        var temptime=parseInt(temp[0])*100+parseInt(temp[1]);
        var temptime2=parseInt(temp2[0])*100+parseInt(temp2[1]);
        if(currtime>=temptime && currtime<temptime2){
            result=1;
        }else{
            result=0;
        }
    }
    return result;
}

function valore(){
    if(msg.Phase_A_power!=null){
        result=msg.Phase_A_power;
    }else if(msg.Phase_B_power!=null){
        result=msg.Phase_B_power;
    }else if(msg.Phase_C_power!=null){
        result=msg.Phase_C_power;
    }else if(msg.power!=null){
        result=msg.power;
    }else{
        return "error";
    }
    return result;
}

