function operativo(array,hour,minute){
    var currtime = hour * 100 + minute;
    var temp=1;
    var result=0;
    for(var i=0;i<array.length;i+=2){
        var t=i+1;
        var time=array[i];
        var time2=array[t];
        if(currtime>=time && currtime<time2){
            result=temp;
        }
        temp+=1;
    }
    return result;
}

