configuration.tbelScript:
if(msg.LowBatteryAlarm==1){return ['LowBatteryAlarm'];}
else if(msg.EmptyPipe==1){return ['EmptyPipe'];}
else if(msg.EmptyTube==1){return ['EmptyPipe'];}
else if(msg.ReverseFlow==1){return ['ReverseFlow'];}
else if(msg.OverRange==1){return ['OverRange'];}
else if(msg.TemperatureAlarm==1){return ['TemperatureAlarm'];}
else if(msg.EEError==1){return ['EEError'];}
else if(msg.LeakageAlarm==1){return ['LeakageAlarm'];}
else if(msg.BurstAlarm==1){return ['BurstAlarm'];}
else if(msg.BatteryOver==1){return ['BatteryOver'];}
else if(msg.BatteryWarning==1){return ['BatteryWarning'];}
else if(msg.BatteryError==1){return ['BatteryError'];}
else if(msg.AccuracyUnsafe==1){return ['AccuracyUnsafe'];}
else if(msg.HardwareError==1){return ['HardwareError'];}
else if(msg.FlowOutOfRange==1){return ['FlowOutOfRange'];}
else if(msg.Sleep==1){return ['Sleep'];}
else if(msg.NotProtected==1){return ['NotProtected'];}
else if(msg.NdcModuleBatteryWarning==1){return ['NdcModuleBatteryWarning'];}
else if(msg.NdcModuleConnectionLost==1){return ['NdcModuleConnectionLost'];}
else{return ['ok'];}

return nextRelation(metadata, msg);

