configuration.jsScript:
var d = new Date();
var hour = d.getHours();
var amperage, energy, frequency, power, voltage;
switch(hour) {
  case 8:
  case 9:
  case 10:
  case 11:
  case 12:
  case 13:
  case 14:
  case 15:
  case 16:
  case 17:
    amperage = (Math.random() * (5 - 0) + 10).toFixed(1);
    frequency = (Math.random() * (50 - 0) + 10).toFixed(1);
    power = Math.random() * (970 - 0) + 30;
    power = Math.round( power * 100 ) / 100;
    energy = power;
    voltage = (Math.random() * (10 - 0) + 220).toFixed(2);
    break;
  default:
    amperage = 0.1 + (Math.random() * (0.1 - 0) + 0).toFixed(1);
    frequency = 10;
    power = Math.random() * (40 - 0) + 10;
    energy = power;
    voltage = 0.5;
}
amperage = Math.round( amperage * 100 ) / 100;
frequency = Math.round( frequency * 100 ) / 100;
voltage = Math.round( voltage * 100 ) / 100;
var msg = {
    amperage: amperage,
    energy: energy,
    frequency: frequency,
    power: power,
    voltage: voltage
};
var metadata = {};
var msgType = "POST_TELEMETRY_REQUEST";

return { msg: msg, metadata: metadata, msgType: msgType };

