    calcoloCosto(F1,"F1");
}
//Se F23 non è nullo
if(F23!=null){
    //Se corrisponde a F23
    if(day=="domenica" || day=="sabato" || ((day!="domenica" || day!="sabato") && (ora>=inizioFascia23 || ora<fineFascia23))){
