            msg.Phase_A_F1=msg.Phase_A_energy;
            msg.Phase_A_F1cost=(msg.Phase_A_energy)*fascia;
        }else if(msg.Phase_B_energy>0){
            msg.Phase_B_F1=msg.Phase_B_energy;
            msg.Phase_B_F1cost=(msg.Phase_B_energy)*fascia;
        }else if(msg.Phase_C_energy>0){
            msg.Phase_C_F1=msg.Phase_C_energy;
            msg.Phase_C_F1cost=(msg.Phase_C_energy)*fascia;
        }
    }else if(nomeFascia=="F2"){
        if(power>0){
            msg.powerCost=(power)*fascia;
            msg.F1=0;
            msg.F2=power;
            msg.F3=0;
            msg.F1cost=0;
            msg.F2cost=(power)*fascia;
            msg.F3cost=0;
        }else if(msg.Phase_A_energy>0){
            msg.Phase_A_F2=msg.Phase_A_energy;
            msg.Phase_A_F2cost=(msg.Phase_A_energy)*fascia;
        }else if(msg.Phase_B_energy>0){
            msg.Phase_B_F2=msg.Phase_B_energy;
            msg.Phase_B_F2cost=(msg.Phase_B_energy)*fascia;
        }else if(msg.Phase_C_energy>0){
            msg.Phase_C_F2=msg.Phase_C_energy;
            msg.Phase_C_F2cost=(msg.Phase_C_energy)*fascia;
        }
    }else if(nomeFascia=="F3"){
        if(power>0){
            msg.powerCost=(power)*fascia;
            msg.F1=0;
            msg.F2=0;
            msg.F3=power;
            msg.F1cost=0;
            msg.F2cost=0;
            msg.F3cost=(power)*fascia;
        }else if(msg.Phase_A_energy>0){
            msg.Phase_A_F3=msg.Phase_A_energy;
            msg.Phase_A_F3cost=(msg.Phase_A_energy)*fascia;
        }else if(msg.Phase_B_energy>0){
            msg.Phase_B_F3=msg.Phase_B_energy;
            msg.Phase_B_F3cost=(msg.Phase_B_energy)*fascia;
        }else if(msg.Phase_C_energy>0){
            msg.Phase_C_F3=msg.Phase_C_energy;
            msg.Phase_C_F3cost=(msg.Phase_C_energy)*fascia;
        }
    }else if(nomeFascia=="F23"){
        if(power>0){
            msg.powerCost=(power)*fascia;
            msg.F23=power;
            msg.F23cost=(power)*fascia;
        }else if(msg.Phase_A_energy>0){
            msg.Phase_A_F23=msg.Phase_A_energy;
            msg.Phase_A_F23cost=(msg.Phase_A_energy)*fascia;
        }else if(msg.Phase_B_energy>0){
            msg.Phase_B_F23=msg.Phase_B_energy;
            msg.Phase_B_F23cost=(msg.Phase_B_energy)*fascia;
        }else if(msg.Phase_C_energy>0){
            msg.Phase_C_F23=msg.Phase_C_energy;
            msg.Phase_C_F23cost=(msg.Phase_C_energy)*fascia;
        }
    }
}

