var temperature = 18 + Math.random() * (32 - 18);
temperature = Math.round( temperature * 100 ) / 100;
var humidity = 10 + Math.random() * (90 - 10);
humidity = Math.round( humidity * 100 ) / 100;
var soundAvg = 30 + Math.random() * (90 - 10);
soundAvg = Math.round( soundAvg * 100 ) / 100;
var soundPeak = 50 + Math.random() * (90 - 10);
soundPeak = Math.round( soundPeak * 100 ) / 100;
var light = 100 + Math.random() * (500 - 20);
light = Math.round( light * 100 ) / 100;
var user_latitude = 44.8923521;
var user_longitude = 11.6176377;
var vdd = 3718;
var msg = { 
            temperature: temperature,
            humidity: humidity,
            soundAvg: soundAvg,
            soundPeak: soundPeak,
            light: light,
            user_latitude: user_latitude,
            user_longitude: user_longitude,
            vdd: vdd
};
return { msg: msg, metadata: {}, msgType: "POST_TELEMETRY_REQUEST" };

