function calcoloCosto(fascia){
    if(msg.power>0){
        msg.powerCost=msg.power*fascia;
    }else if(msg.Phase_A_power>0){
        msg.Phase_A_powerCost=msg.Phase_A_power*fascia;
    }else if(msg.Phase_B_power>0){
        msg.Phase_B_powerCost=msg.Phase_B_power*fascia;
    }else if(msg.Phase_C_power>0){
        msg.Phase_C_powerCost=msg.Phase_C_power*fascia;
    }
}
