var d = new Date();
var hour = d.getHours();
var a_voltage = 220 + Math.random() * (240 - 220);
var a_import_energy ;
var a_export_energy;
var max_current_produced;
var max_power_requested;
//var metatata = { data: 40 } ;

// Imposto una produzione ridotta per la sera ed a zero per la notte
// Imposto il consumo ridotto per la notte, con un minimo per il frigorifero e lucette varie
switch(hour) {
  case 17:
    max_current_produced = 14;
    max_power_requested = 2500;
    break;
  case 18:
    max_current_produced = 10;
    max_power_requested = 2500;
    break;
  case 19:
    max_current_produced = 6;
    max_power_requested = 2000;    
    break;  
  case 20:
    max_current_produced = 0;
    max_power_requested = 1500;    
    break;  
  case 21:
    max_current_produced = 0;
    max_power_requested = 1000;    
    break;    
  case 22:
    max_current_produced = 0;
    max_power_requested = 800;    
    break;    
  case 23:
    max_current_produced = 0;
    max_power_requested = 250;    
    break;    
  case 0:
    max_current_produced = 0;
    max_power_requested = 250;    
    break;    
  case 1:
    max_current_produced = 0;
    max_power_requested = 250;    
    break;    
  case 2:
    max_current_produced = 0;
    max_power_requested = 250;
    break;    
  case 3:
    max_current_produced = 0;
    max_power_requested = 250;    
    break;    
  case 4:
    max_current_produced = 0;
    max_power_requested = 250;    
    break;    
  case 5:
    max_current_produced = 0;
    max_power_requested = 250;    
    break;    
  case 6:
    max_current_produced = 0;
    max_power_requested = 400;    
    break;    
  case 7:
    max_current_produced = 4;
    max_power_requested = 600;    
    break;    
  default:
    max_current_produced = 14;
    max_power_requested = 2500;
}

// Calcolo la produzione di energia con Corrente x Tensione
var a_current = 0 + Math.random() * (max_current_produced - 0); 
var a_power_requested = 250 + Math.random() * (max_power_requested - 250);    
var a_power_produced = a_voltage * a_current;

if (a_power_produced >= a_power_requested) 
{
    a_import_energy = 0;
    a_export_energy = a_power_produced - a_power_requested;
} 
else 
{
// Metto la quantità di energia acquistata come numero negativo, per avere un grafico più utile da leggere    
    a_import_energy = (a_power_requested - a_power_produced) * -1;
    a_export_energy = 0;
}

var msg = { 
//ora: hour,    
a_voltage: a_voltage, 
a_current: a_current,
a_power_produced: a_power_produced,
a_power_requested: a_power_requested,
a_import_energy: a_import_energy,
a_export_energy: a_export_energy
};
return { msg: msg, metadata: { data:40 }, msgType: "POST_TELEMETRY_REQUEST" };
