configuration.tbelScript:
var metadata = { data: 40 };
var msgType = "POST_TELEMETRY_REQUEST";
var hour = new Date().getHours();

var kw = 500 + Math.random() * (1800 - 500);
kw = Math.round( kw * 100 ) / 100;

// Calcolo Importi per Tariffa
if (hour>=0 && hour<=8) {
    var Tariffa1 = kw * (Math.random() * (10 - 0));
    var Tariffa2 = 0;
    var Tariffa3 = 0;
}else if (hour>=9 && hour<=16) {
    var Tariffa2 = kw * (Math.random() * (10 - 0));
    var Tariffa1 = 0;
    var Tariffa3 = 0;    
}else if (hour>=17 && hour<=23) {
    var Tariffa3 = kw * (Math.random() * (10 - 0));
    var Tariffa2 = 0;
    var Tariffa1 = 0;    
}else {
    var Tariffa3 = kw * (Math.random() * (10 - 0));
}

// Calcolo Tipologia Oraria
if (hour>=8 && hour<=12) {
    var Operativo = kw;
    var Pranzo = 0;
    var NonOperativo = 0;
}else if (hour>=13 && hour<=14) {
    var Pranzo = kw;
    var Operativo = 0;
    var NonOperativo = 0;    
}else if (hour>=15 && hour<=18) {
    var Operativo = kw;
    var NonOperativo = 0;
    var Pranzo = 0;    
}else {
    var NonOperativo = kw;
}

// Calcolo Tipologia Carico
if (kw>=500 && kw<=600) {
    var LowCharge = kw;
    var NormalCharge = 0;
    var HighCharge = 0;
}else if (kw>=601 && kw<=1300) {
    var LowCharge = 0;
    var NormalCharge = kw;
    var HighCharge = 0;
}else if (kw>=1301 && kw<=1800) {
    var LowCharge = 0;
    var NormalCharge = 0;
    var HighCharge = kw;
}


var msg = { 
            kw: kw,
            tariffa1: Tariffa1,
            Tariffa2: Tariffa2,
            Tariffa3: Tariffa3,  
            Operativo = Operativo;
            Pranzo = Pranzo;
            NonOperativo = NonOperativo;
            LowCharge = LowCharge;
            NormalCharge = NormalCharge;
            HighCharge = HighCharge;            

};


return { msg: msg, metadata: metadata, msgType: msgType };
